	function dynamicContent() {

		// ********** MYSTART ********** //
		$(".ac-dropdown-container .selector").on('click keypress', function(event){
	        if(globalClick(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
	            event.preventDefault();
	            controlDropdowns(event);
	        }
	    });

		//GLOBALLY CLOSE DROPDOWNS WHEN HOVERED AWAY FROM
	    $(".ac-dropdown-container").hover(function(e) {
			e.preventDefault();
		}, function() {

			$(".ac-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
			$(this).removeClass("open"); 
		});

		//SHOW USER OPTIONS IF THEY ARE LOGGED IN
		if($("#sw-mystart-account").length){
        	$("#ac-user-options .user").show();
            $("#sw-mystart-mypasskey").appendTo("#gb-mystart-left");
        } else {
        	 //HIDE REGISTER BUTTON IF HIDDEN IN SITE MANAGER
              if(!$("#ui-btn-register").length){
                  $("#sign-in-link").show();
              } else {
              	$("#ac-user-options .guest").show();
              }
        }

		// ********** HEADER ********** //

		//SCHOOL NAME
		var multiLineSchoolName = false;

		if(multiLineSchoolName == true){
			var schoolNameLineOne = '';
			var schoolNameLineTwo = '';

			$("#gb-sitename h1, h2.footer-title").html(schoolNameLineOne+"
"+schoolNameLineTwo+"");
			$("#gb-sitename, #gb-footer").addClass("multi-line");
			$("#gb-sitename h1, h2.footer-title").show();
		} else {
			$("#gb-sitename h1, h2.footer-title").show();
		}

		//SOCIAL ICON HOVERS (APPLIES TO FOOTER ICONS AS WELL)
		 $("a.social-icon").hover(function() {
			$(this).parent().addClass("hover");
			$(this).addClass("hover");
		}, function() {
			$(this).parent().removeClass("hover");
			$(this).removeClass("hover");
		});

        $("a.social-icon").focus(function() {
			$(this).parent().addClass("hover");
			$(this).addClass("hover");
		});

        $("a.social-icon").focusout(function() {
			$(this).parent().removeClass("hover");
			$(this).removeClass("hover");
		});

		// SEARCH
		$(".channel-icon.search").on('click keypress', function(event){
	        if(globalClickForButtons(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
	            event.preventDefault();
	            if($(this).hasClass("open")){
	            	$("#sw-mystart-search").slideUp(300, "swing");
					$(this).removeClass("open").attr("aria-label","Open Search Bar");
	            } else {
		            $("#sw-mystart-search").slideDown(300, "swing", function(){
						setTimeout(function(){
							$("#sw-search-input").focus();
						}, 100);
					});
					$(this).addClass("open").attr("aria-label","Close Search Bar");
	            }
    		}
    	});

		// ********** CHANNEL BAR ********** //
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

		// ********** BODY ********** //

        //SUBPAGE EVENTS VIEW CALENDAR LINK
		$("#gb-page.sp .upcomingevents").each(function(){
			$(this).find(".view-calendar-link").addClass("more-link").html("More Events").show();
		})

		//SUBPAGE HEADLINES READ MORE
		$("#gb-page.sp .headlines").each(function(){
			$(this).find(".more-link span").text("More News");
		})

		//SUBPAGE ANNOUNCEMENTS READ MORE
		$("#gb-page.sp .announcements").each(function(){
			$(this).find(".more-link span").text("More Announcements");
		})

		//FIX FIRST BREADCRUMB
		$(".ui-breadcrumb-first span").text("Home").show();

		//CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
    	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
    		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
