	function rsMenu(mobileSchoolHeadings) {

        //ADD HEADER LINKS TO MOBILE MENU FOR 480 AND BELOW
        var popLinks = [
                ["Employees", "https://www.gaston.k12.nc.us/employees", "_self"], // ARRAY ORDER - TEXT, LINK, TARGET
                ["Transcripts", "https://www.gaston.k12.nc.us/studentrecords", "_self"], // ARRAY ORDER - TEXT, LINK, TARGET
                ["Jobs", "https://www.gaston.k12.nc.us/humanresources", "_self"], // ARRAY ORDER - TEXT, LINK, TARGET
                ["Departments", "https://www.gaston.k12.nc.us/departments", "_self"], // ARRAY ORDER - TEXT, LINK, TARGET
                ["En Espanol", "http://translate.google.com/translate?u=https%3A%2F%2Fwww.gaston.k12.nc.us%2F&langpair=en|es&hl=en&anno=0&ie=UTF8", "_self"] // ARRAY ORDER - TEXT, LINK, TARGET
            ];

            var mobileHeaderLinks = "";

            $.each(popLinks, function(k, v){
                if( v[0] != "" ) mobileHeaderLinks += "1, " + v[0] + ", " + v[1] + ", " + v[2] + ";";
            });    

    	head.ready("rsMainMenu", function(){
            // RESPONSIVE MAIN MENU
            $("body").csRsMenu({
                "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
                "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
                "primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
                "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
                "accentColor"           : "#BABABA", // DEFAULT - #BABABA
                "menuButtonParent"      : "#header-right-inner",
                "showMenuBtnText"       : "yes",
                "menuBtnText"           : "MENU",
                "showSchools"           : "no",
                "showTranslate"         : "yes",
                "translateId"           : "none",
                "showAccount"           : "no",
                "extraMenuOptions"      : "Quick Links;"+mobileSchoolHeadings,
                "extraMenuItems" 		: mobileHeaderLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
                "siteID"                : "4",
                "allLoaded"             : function(){ mobileSchools(); }
            });
		});
    }

    function mobileSchools(){
    	//FIX ANY CLASSES ON SCHOOL LIST ITEMS THAT MIGHT HAVE A / IN THEM
        $(".rs-menu-option").each(function(){
        	var menuClasses = $(this).attr("class");
            menuClasses = menuClasses.replace("/","-");
            $(this).attr("class",menuClasses);
        })

    	//LOOP THROUGH TAB PANELS IN THE SCHOOL MENU CONTAINER
        $(".school-listing-container").each(function(){
        	var schoolType = $(this).attr("id");

            //CLONE THE LIST ITEMS AND PLACE THEM INTO THE CORRECT LISTS IN THE MOBILE MENU
            $(this).find(".school-list").children().clone().appendTo(".rs-menu-option."+schoolType+" .rs-menu-option-sublist");
        })

        //HIDE TRANSLATE IF NECESSARY
        var translateToggle = true;

        if(translateToggle != true){
        	$(".rs-menu-option.translate").hide();
        }

        //ADD HOME AND CALENDAR BUTTONS INTO MENU
        $(".rs-menu-option-list").prepend("
"+
        	"
"+
        		""+
        			"Home"+
        		""+
        	""+
        ""+
        "
"+
        	"
"+
        		""+
        			"Calendar"+
        		""+
        	""+
        "");
    }
