	function accessibilityFunctions(){
        $("#sw-search-button").attr({
        	"aria-label":"Click to search",
        	"role":"button"
        });

		$("#sw-mystart-mypasskey").attr({
			"aria-label":	"Open My Passkey",
			"role":		"button"
		});

		// KEY MAPPING
		var csKeyCodes = { "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40, "tab": 9 };

		//GLOBAL KEYBOARD NAV FOR DD SELECTORS
		$(".ac-dropdown-container .selector").keydown(function(e) {
			var parentContainer = $(e.currentTarget).closest(".ac-dropdown-container").attr("class");
            var parentContainer = parentContainer.split(" ");
            var parentContainer = parentContainer[1];

			// CAPTURE KEY CODE
			switch(e.keyCode) {

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						$(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container."+parentContainer+"").removeClass("open");

						var ariaLabel = $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-label");
			            ariaLabel = ariaLabel.split(" ");
			            var newAriaLabel = "Expand "+ariaLabel[1]+" "+ariaLabel[2];
						$(".ac-dropdown-container."+parentContainer+" .selector").attr({
			            	"aria-expanded":"false",
			                "aria-label":newAriaLabel
			            })
					} else {
						e.preventDefault();
						$(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container."+parentContainer+"").removeClass("open");

						var ariaLabel = $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-label");
			            ariaLabel = ariaLabel.split(" ");
			            var newAriaLabel = "Expand "+ariaLabel[1]+" "+ariaLabel[2];
						$(".ac-dropdown-container."+parentContainer+" .selector").attr({
			            	"aria-expanded":"false",
			                "aria-label":newAriaLabel
			            })

						switch(parentContainer){
							case "user-options":
								$(".gb-bb-footer.links").find("a:first-child").focus();
							break;
							case "translate":
								if($("#header-right-inner .gb-social-icons").attr("data") == "true"){
									//HEADER SOCIAL IS HIDDEN, FOCUS ON SCHOOLS
									$("#our-schools-button").focus();
								} else {
									$("#header-right-inner .gb-social-icons").find(".social-icon:first-child").focus();
								}
							break;
						}
					}
				break;

				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.down:
				case csKeyCodes.right:
					e.preventDefault();

					$(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();

				break;
			}
		});

		//GLOBAL KEYBOARD NAV FOR DD LINKS
		$(".ac-dropdown-container li a").keydown(function(e) {
            var parentContainer = $(e.currentTarget).closest(".ac-dropdown-container").attr("class");
            var parentContainer = parentContainer.split(" ");
            var parentContainer = parentContainer[1];
            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(".ac-dropdown-container."+parentContainer+" .selector").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						e.preventDefault();
						// IS FIRST ITEM
						if($(this).parent().is(":first-child")) {
							// FOCUS DROPDOWN BUTTON
							$(".ac-dropdown-container."+parentContainer+" .selector").focus();
						} else {
							// FOCUS PREVIOUS ITEM
							$(this).parent().prev("li").find("> a").focus();
						}
					} else {
						e.preventDefault();

						// IS LAST ITEM
						if($(this).parent().is(":last-child")) {
							// FOCUS FIRST ITEM
							$(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
						} else {
							// FOCUS NEXT ITEM	
							$(this).parent().next("li").find("> a").focus();
						}
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:last-child > a").focus();
				break;

				case csKeyCodes.esc:
					$(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container."+parentContainer+"").removeClass("open");
						$(".ac-dropdown-container."+parentContainer+" .selector").focus();
				break;
			}
		});

		//SEARCH BUTTON
		$("#sw-search-button").keydown(function(e){
			switch(e.keyCode) {
				// CONSUME ENTER AND SPACE KEYS
				case csKeyCodes.enter:
				case csKeyCodes.space:
					e.preventDefault();

					$(this).click();

				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){

					} else {
						e.preventDefault();
						$("li.sw-channel-item:first-child > a").focus();
						$("#sw-mystart-search").slideUp(300, "swing");
						$(".channel-icon.search").removeClass("open").attr("aria-label","Open Search Bar");
					}
				break;
			}

		});

		//SEARCH INPUT
		$("#sw-search-input").keydown(function(e){
			switch(e.keyCode) {

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						e.preventDefault();
						$(".channel-icon.search").focus();
						$("#sw-mystart-search").slideUp(300, "swing");
						$(".channel-icon.search").removeClass("open").attr("aria-label","Open Search Bar");
					}
				break;
			}

		});

		//SEARCH
		$(".channel-icon.search").keydown(function(e) {

            // CAPTURE KEY CODE
			switch(e.keyCode) {

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){

					} else {
						e.preventDefault();

						if($(this).hasClass("open")){
							$("#sw-search-input").focus();
						} else {
							$("li.sw-channel-item:first-child > a").focus();
						}

					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:last-child > a").focus();
				break;

				case csKeyCodes.esc:
					$(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container."+parentContainer+"").removeClass("open");
						$(".ac-dropdown-container."+parentContainer+" .selector").focus();
				break;
			}
		});

		//MEGA SCHOOL MENU

		//SCHOOL TABS
		$(".school-tab").keydown(function(e) {

            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$("#close-school-list").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).prev().focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".school-tab:first-child").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).next().focus();
					}
				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						e.preventDefault();
						// IS FIRST ITEM
						$("#our-schools-button").focus();
					} else {
						e.preventDefault();

						$(".school-listing-container.active li:first-child a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".school-tab:first-child").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$("#close-school-list").focus();
				break;

				case csKeyCodes.esc:
					$("#school-list-container").slideUp(300, "swing").attr("aria-hidden","true");
                	$(this).attr("aria-expanded","false");
                	$("#our-schools-button, #close-school-list").removeClass("open");
                	setTimeout(function(){
						$("#our-schools-button").focus();
					}, 100);
				break;
			}
		});

		//CLOSE SCHOOLS BUTTON
		$("#close-school-list").keydown(function(e) {

            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					$(this).prev().focus();

				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					$(".school-tab:first-child").focus();

				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						e.preventDefault();

						$("#our-schools-button").focus();

					} else {
						e.preventDefault();

						$(".school-tab:first-child").focus();

					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".school-tab:first-child").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(this).focus();
				break;

				case csKeyCodes.esc:
					$("#school-list-container").slideUp(300, "swing").attr("aria-hidden","true");
                	$(this).attr("aria-expanded","false");
                	$("#our-schools-button, #close-school-list").removeClass("open");
                	setTimeout(function(){
						$("#our-schools-button").focus();
					}, 100);
				break;
			}
		});

		//SCHOOLS
		$("li.school a").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					if($(this).parent().is(":first-child")){
						$(".school-listing-container.active").find("li.school:last-child a").focus();
					} else {
						$(this).parent().prev().find("> a").focus();
					}

				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					if($(this).parent().is(":last-child")){
						$(".school-listing-container.active").find("li.school:first-child a").focus();
					} else {
						$(this).parent().next().find("> a").focus();
					}

				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".school-listing-container.active").find("li.school:first-child a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".school-listing-container.active").find("li.school:last-child a").focus();
				break;

				case csKeyCodes.esc:
					$("#school-list-container").slideUp(300, "swing").attr("aria-hidden","true");
                	$(this).attr("aria-expanded","false");
                	$("#our-schools-button, #close-school-list").removeClass("open");
                	setTimeout(function(){
						$("#our-schools-button").focus();
					}, 100);
				break;
			}
		});

        //REMOVE UNUSED LINKS
       $("#gb-header-top a").each(function(){
        	var linkFlag = $(this).attr("data");

            if(linkFlag == "" || linkFlag == " "){
            	$(this).remove();
            } 
        });

	}
