$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=%2f7m4b6%2fm1j5tAana4VoOEA%3d%3d&SessionID=66c11c80-46d1-4ddf-9c6f-76ef1a2dee22&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f20%2f2020+1%3a23%3a07+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.85.142';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
      // prevent site error for analytics
   }
