div.script:
document.addEventListener('DOMContentLoaded', function() {
	$("#sitenav_tertiary").accordion({
		header: ".accordion-label",
		collapsible: true,
		active: false,
		event:  "mouseover",
	}).on('mouseleave', function(){
		$(this).accordion('option', 'active', false);
	});
});

(function(d, s, id) {
				var js, fjs = d.getElementsByTagName(s)[0];
				if (d.getElementById(id)) return;
				js = d.createElement(s); js.id = id;
				js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.7";
				fjs.parentNode.insertBefore(js, fjs);
			}(document, 'script', 'facebook-jssdk'));

document.addEventListener('DOMContentLoaded', function() {
	var tabpanel_panel = new tabpanel("tabpanel_container", false);
});

body.script:
window.onload = init;
var topnavButton = document.getElementById('sitenav_main_mobile_toggle');
var topnavDisplay = document.getElementById('sitenav_main');

function init() {
	topnavButton.onclick = toggleNav;
}

function toggleNav(){
	topnavDisplay.classList.toggle("open");
	topnavButton.classList.toggle("open");
}

window.addEventListener('scroll', function(event) {
    var toggleElement = document.getElementById('social_nav_header');

    if ( window.pageYOffset > 300 ) {
        toggleElement.classList.add('fixed');
        return;
    } else {
        toggleElement.classList.remove('fixed');
        return;
    }
});
