    });

    function resize(){
    	var vpw = $(window).width();

        if(vpw < 1024){
        	$('.cs-custom-app-detail').removeClass('cs-jfs').addClass('cs-jc');
            $('#gb-footer, #gb-footer-imgs').removeClass('cs-jsb').addClass('cs-jc');
            if($('.cs-custom-app-header .mobile-arrow').size() == 0){
            	$('.cs-custom-app-header').attr({
                	"role": "button",
                    "aria-expanded": "false",
                    "tabindex": "0"
                }).append(mArrow);
            }
            $('#hp-content-lower .ui-widget.app').each(function(k, v){
            	if($('.ui-widget-header .mobile-arrow', v).size() == 0){
                	$('.ui-widget-header', v).append(mArrow);
                }
            });
            csApps();
        } else {
        	$('.cs-custom-app-detail').addClass('cs-jfs').removeClass('cs-jc');
            $('#gb-footer, #gb-footer-imgs').addClass('cs-jsb').removeClass('cs-jc');
            $('.cs-custom-app-header').removeAttr("tabindex role aria-expanded").unbind('click');
            $('#hp-content-lower .ui-widget-header').unbind('click');
        }
    }

    function csApps(){
    	$('.cs-custom-app-header').unbind('click').click(function(){
        	if($(this).hasClass('open')){
            	$(this).attr("aria-expanded", "false").removeClass('open');
                $('.cs-custom-app-detail').slideUp();
            } else {
            	$(this).attr("aria-expanded", "true").addClass('open');
                $('.cs-custom-app-detail').slideDown().css('display', 'flex');
            }
        });
        $('#hp-content-lower .ui-widget-header').unbind('click').click(function(){
        	if($(this).hasClass('open')){
            	$(this).removeClass('open').siblings('.ui-widget-detail').slideUp();
            } else {
            	$(this).addClass('open').siblings('.ui-widget-detail').slideDown();
            }
        });
    }


	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE7){ $("body").addClass("msie7"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }
		if(isMSIE11){ $("body").addClass("msie11"); }
		$("body").addClass(navigator.platform + " " + browserName + " " + browserName + browserVersion);

		$(window).bind("resize", function(){ 
			calendarListView();
		});

        //--> Build Out Mobile Menu Icons
        var gbIcons = [
                ['true','Enroll Now', '/Page/395'],
                ['true','Calendar', 'https://www.clevelandmetroschools.org/Page/2'],
                ['true','Jobs', '/jobs']
        	],
        	iconHTML = '';
        for(var icon in gbIcons){
        	if(gbIcons[icon][0]){
            	iconHTML += '1, ' + gbIcons[icon][1] + ', ' + gbIcons[icon][2] + ', _blank;'; 
            }
        }


		// RESPONSIVE MENU
		$("body").csRsMenu({
			"breakPoint"		: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
		        "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
		        "accentColor"           : "#BABABA", // DEFAULT - #BABABA
			"menuButtonParent"	: "#gb-mystart-outer",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "MENU",
			"showSchools"		: "no",
			"showTranslate"		: "no",
			"translateId"		: "none",
			"showAccount"		: "yes",
			"extraMenuOptions"	: "Global Icons", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"	: iconHTML + "", // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"		: "4"
		});

		// RESPONSIVE PAGELIST MENU
		$("body").csRsPagelistMenu({
			"breakPoint"		: 480, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
		        "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
		        "accentColor"           : "#BABABA", // DEFAULT - #BABABA
			"menuButtonParent"	: "#sp-content",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "PAGES",
			"siteID"			: "4"
		});
		useChannelForSection();
		dynamicContent();
        resize();
	});


	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){

    	//--> Remove Skip Nav Button
        $('.sw-skipnav-outerbar').remove();

		// ********** MYSTART ********** //
		//ADD TRANSLATOR
