    function hpTiles(){
        /* Challenge Excepted!
         *
         * Thanks for the Idea Brenton!
    	 */

        //--> So it Begins
        var appHTML = '',

        //--> Tile Number
        	tile = 1;

        //--> Get Region B Header Text
        var regionBText = $('.hp.row.one .ui-widget.headlines .ui-widget-header').first().text();

        //--> Set Region B Header Text
        $('#cs-custom-app .cs-custom-app-header h1').append(regionBText);


        //--> Build Tiles
        $('#cs-app-staging .ui-widget.app').each(function(k, v){
        	//--> If HF App pull each Article Content
        	if($(v).hasClass('headlines')){

            	//--> Get Each Article Content
                $('.ui-articles li', v).each(function(m, n){

                    //--> Get Title Text
                    var text = $('.ui-article-title a span', n).html().split('
'),

                    	//--> Get Date
                    	date = text[0],

                        //--> Get Title
                    	title = text[1];

                    //--> Set Date and Title 
                    //--> $('.ui-article-title', n).prepend('' + date + '
');
                    $('.ui-article-title a', n).html(title);

                    //--> Get Article Link
                    var link = $('h1 a', n).attr('href');

                    //--> Create Custom Read More Link
                    if(tile != 3){  
                        $('.ui-article-title', n).append('Read more...');
                    }

                    //--> If Third Tile Add Large More Button w/ Watch Video 
                    if(tile == 3){    
                        $('.ui-article', n).append('
WATCH VIDEO ' + playIcon + '
Read More' + moreIcon + '');
                    	if(true){
                        	$('.custom-watch').show();
                        }
                    }

                    //--> Check if Image Exist on article
                    if($('.img img', n).attr('height') != 0){
                        appHTML += '
' + $(n).html() + '';
                    } else {
                        appHTML += '
' + $(n).html() + '';
                    }

                    //--> Increase Tile Number
                    tile++;
                });

                //--> Add News Image Tile 
                //--> appHTML += '
';

                //--> Increase Tile Number
                //--> tile++;

            //--> If Flex or Content App
            } else if($(v).hasClass('flexpage')) {

                //--> Get App Content
                appHTML += '
' + $('.ui-widget-detail', v).html() + '';

                //--> Increase Tile Number
                tile++;

            //--> If Upcoming Evnets App
            } else if($(v).hasClass('upcomingevents')){

                //--> Add App Contents
                appHTML += '
'+ $(v).html() + '';


                //--> Increase Tile Number
                tile++;

                //--> Add Events Image Tile 
                //-->appHTML += '
';

                //--> Increase Tile Number
                //-->tile++;
            }
        });


        //--> Add All Apps tp Upper Content;
        $('#cs-custom-app .cs-custom-app-detail').html(appHTML);
    	$('#cs-app-staging').remove();

    }

    // LOAD THE IFRAME API
    var tag = document.createElement("script");
    tag.src = "https://www.youtube.com/iframe_api";
    var firstScriptTag = document.getElementsByTagName("script")[0];
    firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

	var youtubeVideoPlayers = [];
    var titles = [];


    function onYouTubeIframeAPIReady() {
    	setTimeout(function(){
            var player;

            /*
            	9.1.17 - Brenton Kelly
                In latest release, .sw-flex-item-group element no longer exists in Minibase custom view.
                Some of the following JS was written based on the old structure and I commented out and adjusted what was needed.
                I also adjusted the custom view structure in the Minibase.
            */

            //--> Assign div vid function
            //$('#cs-vid-slider').parent().removeClass('sw-flex-item-group').addClass('cs-vid-functions');

            //--> Assign Parent wrapper
            //$('#cs-vid-next').parent().removeClass('sw-flex-item-group').attr('id', 'cs-video-control-wrapper');

            //--> Assign slide wrapper
            //$('#cs-vid-slider').parent().parent().addClass('cs-slides-wrapper');
            $('#cs-vid-slider').parent().addClass('cs-slides-wrapper');

            //--> Assign New App Name
            //$('#cs-vid-slider').parent().parent().parent().parent().removeClass('minibase').addClass('cs-vid-slider');
            $('#cs-vid-slider').closest(".ui-widget.app").removeClass('minibase').addClass('cs-vid-slider');

            //--> Create Each Slide
            //$('.cs-vid-slider .sw-flex-item-group').each(function(k, v){
            $('.cs-vid-slider .cs-slide').each(function(k, v){

                //--> Slide Number
                var n = k + 1,
                    vidID = $('.cs-vid-id', v).data('vid-id');

                //--> Assign cs-slide Class and Number
                //$(v).removeClass('sw-flex-item-group').addClass('cs-slide').attr('data-vid', n).children('.youtube-video').attr('id', 'youtube-video-' + n);
                $(v).attr('data-vid', n).children('.youtube-video').attr('id', 'youtube-video-' + n);
                $('canvas', v).attr('id', 'youtube-loader-' + n);

                //--> Get Video Titles
                titles.push($('.cs-vid-title', v).data('vid-title'));

                // CREATE THE IFRAME AFTER API IS LOADED

                player = new YT.Player("youtube-video-" + n, {
                    height: 400,
                    width: $(window).width(),
                    videoId: vidID,
                    playerVars: { 
                        "modestbranding": 1,
                        "controls": 0, 
                        "rel" : 0,
                        "loop": 1,
                        "showinfo": 0,
                        "autoplay": 1,
                        "wmode": "opaque"
                    },
                    events: {
                        // YOUTUBE PLAYER IS READY
                        "onReady": function(e) {
                            // SET VIDEO DIMENSIONS
                            youTubeVideoDims();
							var vpw = $(window).width();

                            // MUTE THE VIDEO
                            e.target.mute();
							if(vpw >= 1024){
                                // MAKE SURE TO AUTO PLAY
                                if(n == 1){
                                    e.target.playVideo();
                                } else {
                                    e.target.pauseVideo();
                                }

                            } else {
                            	e.target.stopVideo();
                                $('#cs-vid-play').removeClass('pause').addClass('play');
                            }

                            $('.cs-slide[data-vid="'+n+'"] iframe').attr('title', $('.cs-vid-title', v).data('vid-title')).attr('tabindex', '-1');

                        },
                        "onStateChange": function(e){
                            // VIDEO HAS ENDED
                            if(e.target.getPlayerState() == 0) {
                                e.target.playVideo();
                            }
                        }
                    }
                });

                youtubeVideoPlayers.push(player);
            });


            // BIND VIDEO EVENTS
            $("#cs-vid-play").click(function() {

                // PAUSE
                var player = $('#hp-video-player .cs-slide.active').attr('data-vid');
                player = parseInt(player);
                player = player - 1;
                if($(this).hasClass("pause")) {
                    youtubeVideoPlayers[player].pauseVideo();
                    $(this).removeClass("pause").addClass("play");
                }
                // PLAY
                else {
                    youtubeVideoPlayers[player].playVideo();
                    $(this).removeClass("play").addClass("pause");
                }
            }).keydown(function(e){
            	var c = e.which;

                if(c == kSpace || c == kEnter){
                	e.preventDefault();
                    $(this).click();
                }

            });


            //--> Hide and Reposition Each Slide
            //$('.cs-slide').each(function(k, v){ $(v).addClass('loaded');});

            //--> Show First Slide
            $('.cs-slide[data-vid="1"]').show().addClass('active');

            //--> Add Video Title
            $('.cs-vid-caption').append('
' + $('.active .cs-vid-title').data('vid-title') + '');


            //--> Get Slide Active Number and Total amount of Slides

            $('.cs-vid-btn').unbind('click').click(function(){

                var slide = $('.cs-slide.active').data('vid'),
                    tSlides = $('.cs-slide').size(),
                    vpw = $(window).width();


                //--> Check if Next or Prev
                if($(this).hasClass('next')){
					if(vpw >= 1024){
                        //--> Check if Last Slide
                        if(slide == tSlides){
                            var next = 1,
                                p = slide - 1,
                                t = next - 1;
                        } else {
                            var next = slide + 1,
                                p = slide - 1,
                                t = next - 1;
                        }

                        //--> Change Vid Caption
                        $('.cs-vid-caption h1').html(titles[t]);



                        youtubeVideoPlayers[p].pauseVideo();
                        //--> Change to Next Slide
                        $('.cs-slide[data-vid="' + slide + '"]').animate({
                            opacity: 0
                        }, 500).removeClass('active');
                        $('.cs-slide[data-vid="' + next + '"]').animate({
                            opacity: 1
                        }, 500).addClass('active');
                        youtubeVideoPlayers[t].playVideo();


                        $('#cs-vid-play').removeClass('play').addClass('pause');

                        //$('.cs-slide[data-vid="' + slide + '"]').hide('slide', {direction: 'left'}, 500).removeClass('active');
                        //$('.cs-slide[data-vid="' + next + '"]').show('slide', {direction: 'right'}, 500).addClass('active');
                    } else {
                    	//--> Check if Last Slide
                        if(slide == tSlides){
                            var next = 1,
                                p = slide - 1,
                                t = next - 1;
                        } else {
                            var next = slide + 1,
                                p = slide - 1,
                                t = next - 1;
                        }

                        //--> Change Vid Caption
                        $('.cs-vid-caption h1').html(titles[t]);

                        //--> Change to Next Slide
                        $('.cs-slide[data-vid="' + slide + '"]').animate({
                            opacity: 0
                        }, 500).removeClass('active');
                        $('.cs-slide[data-vid="' + next + '"]').animate({
                            opacity: 1
                        }, 500).addClass('active');

                        $('#cs-vid-play').removeClass('play').addClass('pause');

                    }
                } else {
					if(vpw >= 1024){
                        //--> Check if Last Slide
                        if(slide == 1){
                            var prev = tSlides,
                                p = slide - 1,
                                t = prev - 1;
                        } else {
                            var prev = slide - 1,
                                p = slide - 1,
                                t = prev - 1;
                        }

                        //--> Change Vid Caption
                        $('.cs-vid-caption h1').html(titles[t]);

                        youtubeVideoPlayers[p].pauseVideo();
                        //--> Change to Next Slide
                        $('.cs-slide[data-vid="' + slide + '"]').animate({
                            opacity: 0
                        }, 500).removeClass('active');
                        $('.cs-slide[data-vid="' + prev + '"]').animate({
                            opacity: 1
                        }, 500).addClass('active');
                        youtubeVideoPlayers[t].playVideo();

                        $('#cs-vid-play').removeClass('play').addClass('pause');


                        $('.cs-slide[data-vid="' + slide + '"]').hide('slide', {direction: 'right'}, 500).removeClass('active');
                        $('.cs-slide[data-vid="' + prev + '"]').show('slide', {direction: 'left'}, 500).addClass('active');
					} else {

                    	//--> Check if Last Slide
                        if(slide == 1){
                            var prev = tSlides,
                                p = slide - 1,
                                t = prev - 1;
                        } else {
                            var prev = slide - 1,
                                p = slide - 1,
                                t = prev - 1;
                        }

                        //--> Change Vid Caption
                        $('.cs-vid-caption h1').html(titles[t]);

                        //--> Change to Next Slide
                        $('.cs-slide[data-vid="' + slide + '"]').animate({
                            opacity: 0
                        }, 500).removeClass('active');
                        $('.cs-slide[data-vid="' + prev + '"]').animate({
                            opacity: 1
                        }, 500).addClass('active');

                        $('#cs-vid-play').removeClass('play').addClass('pause');

                    }
                }
            }).unbind('keyup').keyup(function(e){
            	var c = e.which;
                if(c == kEnter || c == kSpace){
                	e.preventDefault();
                	$(this).click();
                } 
            });


        }, 300);
    }

    function youTubeVideoDims() {
        // GET SCREEN DIMENSIONS
        var screenWidth = $(window).width();
        var screenHeight = $(window).height();
        var vidWidth = screenWidth, vidHeight = 0;

        // SET WIDTH, HEIGHT, AND RATIO
        var initW = 853.5, initH = 480;
        var ratio = initH / initW;
        vidHeight = vidWidth * ratio;
        if(vidHeight < screenHeight){
            vidHeight = screenHeight;
            vidWidth = vidHeight / ratio;
        }

        // CENTER VIDEO
        var centerWidth = (vidWidth - screenWidth) / 2;
        // var centerHeight = (vidHeight - screenHeight) / 2;
        var centerHeight = 0;

        // APPLY THE DIMENSIONS
        $(".youtube-video").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
    }

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
	}

