    function LoadMinibaseList16480(PageIndex){
        var FilterFields = "";
        if ($('#sw-content-minibaselist16480-filterquery').length > 0){
        	FilterFields =encodeURIComponent($('#sw-content-minibaselist16480-filterquery').val());
        }else{
            FilterFields = escape(CreateFilterQuery16480());
        }
        var DisplayType = $("#sw-content-minibaselist16480-displaytype").val();
        if (PageIndex === undefined) {
            PageIndex = $("#sw-content-minibaselist16480-pageindex").val();
        }
        var success = '$("#sw-content-minibaselist16480-pageindex").val(' + PageIndex + ');';
        
        if (0 == 1)
        { window.open( "https://www.clevelandmetroschools.org//site/Default.aspx?DomainID=0&PageID=0&PageType=22&ModuleInstanceID=14756&PageModuleInstanceID=16480&FilterFields=" + FilterFields + "&DirectoryType=" + DisplayType + "&PageIndex=" + PageIndex);
        }
        else 
        { GetContent("https://www.clevelandmetroschools.org//site/UserControls/Minibase/MinibaseListWrapper.aspx?ModuleInstanceID=14756&PageModuleInstanceID=16480&FilterFields=" + FilterFields + "&DirectoryType=" + DisplayType + "&PageIndex=" + PageIndex, "sw-content-minibaselist16480", 2, success);
