$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=sfpDvTPFBHTDS4%2f2GRlP6Q%3d%3d&SessionID=8d3de709-f6a8-43a4-b574-ba8905d6f8e7&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f20%2f2020+1%3a33%3a55+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.27.249';
