function linkMover() {
    if (window.innerWidth < 991)
        $(".quicklinks").appendTo(".slicknav_nav");
    else
        $(".quicklinks").appendTo("#logo");
}
$(document).ready(function () {
    linkMover();
});
$(window).resize(function () {
    linkMover();
});

    $(document).konami({
        result: function () {
            var j;
            function reqListener() {
                j = JSON.parse(this.responseText);
                //console.log(j["value"]);
                var cont = document.createElement("div");
                var fact = document.createElement("div");
                var close = document.createElement("div");
                $(close).html('');
                $(close).css({ "color": "white", "position": "absolute", "top": "0px", "right": "0px", "font-size": "24px", "padding": "5px 15px" });
                $(fact).html("" + j["name"]);
                $(fact).css({ "color": "white", "text-transform": "capitalize", "text-shadow": "0 0 10px black", "font-size": "48px", "position": "absolute", "left": "50%", "top": "50%", "transform": "translateX(-50%) translateY(-50%)", "text-align": "center", "line-height": "1.2" });
                $(cont).css({ "background": "rgba(0, 0, 0, 0.65)", "position": "fixed", "top": "0", "left": "0", "width": "100%", "height": "100%", "z-index": "99999999" });
                $(fact).appendTo(cont);
                $(close).appendTo(cont);
                $(cont).appendTo("body");
                $(close).click(function () {
                    $(this).parent().remove();
                });
            }

            var oReq = new XMLHttpRequest();
            oReq.addEventListener("load", reqListener);
            var r = Math.random() * 803;
            oReq.open("GET", "https://pokeapi.co/api/v2/pokemon/" + Math.floor(r.toString()));
            oReq.send();
        }
    });

    function SearchExpand() {
        if ($(".SearchContainer").hasClass("Open")){
            $(".SearchContainer").removeClass("Open");
        } else {
            $(".SearchContainer").addClass("Open");
        }
    }
    if (!window.hasOwnProperty('webkitSpeechRecognition')) {
        $(".GoogleSearch span").remove();
        $(".GoogleSearch input").css("width", "calc(100% - 29px)");
    }
    function startDictation() {

        if (window.hasOwnProperty('webkitSpeechRecognition')) {

            var recognition = new webkitSpeechRecognition();

            recognition.continuous = false;
            recognition.interimResults = false;

            recognition.lang = "en-US";
            recognition.start();

            $(".header_search_box")[0].placeholder = "Listening...";
            $(".header_search_box")[1].placeholder = "Listening...";
            recognition.onresult = function (e) {
                $(".header_search_box")[0].placeholder = "";
                $(".header_search_box")[0].value = e.results[0][0].transcript;
                $(".header_search_box")[1].placeholder = "";
                $(".header_search_box")[1].value = e.results[0][0].transcript;
                recognition.stop();
                $(".header_search_button")[0].click();
            };

            recognition.onerror = function (e) {
                recognition.stop();
            }

            recognition.onend = function (e) {
                $(".header_search_box")[0].placeholder = "Search...";
                $(".header_search_box")[1].placeholder = "Search...";
            }
        }
    }

div.script:
function popUp(URL) {
        day = new Date();
        id = day.getTime();
        eval("page" + id + " = window.open(URL, '" + id + "', 'toolbar=0,scrollbars=1,location=0,statusbar=1,menubar=0,resizable=1,width=700,height=600,left = 440,top = 212');");
    }

