    $(document).konami({
        result: function () {
            var c = document.createElement("canvas");
            $(c).attr("style", "width: 100%; height: 100%; pointer-events: none; position: fixed; z-index: 999999; top: 0; left: 0; cursor: none;");
            $("body *").css("cursor", "none");
            c.width = window.innerWidth;
            c.height = window.innerHeight;
            var emitter = new Emitter(c, { x: c.width / 2, y: c.height / 2 }, { backgroundColor: "transparent", life: 60, startAngle: Math.PI * 1.4, endAngle: Math.PI * 1.6, lifeVariation: 0, alpha: 0.3, alphaChange: -0.007, radius: 2, emitSpeed: 20, speed: 1, acceleration: { x: 0, y: 0.05 }, color: "#030818", size: 1, sizeChange: 0.1 });

            emitter.emit(true);
            document.body.addEventListener("mousemove", function (e) {
                var x = (e.x / (window.innerWidth - 18)) * window.innerWidth;
                emitter.updatePosition({ x: x, y: e.y });
            });
            $(c).appendTo("body");
            function Emitter(canvas, position, options) {
                var pos = position;
                var c = canvas;
                var ctx = c.getContext("2d");
                var options = options || {};
                var size = options.size || 6;
                var sizeChange = options.sizeChange || 0;
                var backgroundColor = options.backgroundColor || "Black";
                var startAngle = options.startAngle || 0;
                var endAngle = options.endAngle || Math.PI * 2;
                var aDiff = endAngle - startAngle;
                var radius = options.radius || 0;
                var color = options.color || "#060";
                var blendMode = options.blendMode || "lighter";
                var life = options.life || 100;
                var lifeVariation = options.lifeVariation || 0;
                var speed = options.speed || 2;
                var speedVariation = options.speedVariation || 0.1;
                var spdStart = speed - speedVariation / 2
                var acceleration = options.acceleration || { x: 0, y: -0.02 };
                var accelerationVariation = options.accelerationVariation || { x: 0, y: 0 };
                var accStart = { x: acceleration.x - accelerationVariation.x / 2, y: acceleration.y - accelerationVariation.y / 2 };
                var emitSpeed = options.emitSpeed || 5;
                var alpha = options.alpha || 1;
                var alphaChange = options.alphaChange || 0;
                var particleList = [];
                var _emit = false;
                this.emit = function (value) {
                    _emit = value;
                }
                setInterval(function () {
                    if (_emit) {
                        AddParticles();
                    }
                    var l = particleList.length;
                    ctx.clearRect(0, 0, c.width, c.height);
                    ctx.fillStyle = backgroundColor;
                    ctx.globalAlpha = 1;
                    ctx.globalCompositeOperation = "source-over";
                    ctx.fillRect(0, 0, c.width, c.height);
                    for (var i = 0; i < l; i++) {
                        var p = particleList[i];
                        var _pos = p.getPosition();
                        var _a = p.getAlpha();
                        var _s = p.getSize();
                        ctx.globalAlpha = _a;
                        ctx.globalCompositeOperation = blendMode;
                        ctx.fillStyle = color;
                        ctx.beginPath();
                        ctx.arc(_pos.x, _pos.y, _s, 0, Math.PI * 2);
                        ctx.fill();
                        ctx.closePath();
                        if (p.update()) {
                            RemoveParticle(i);
                        }
                    }
                }, 20);
                function AddParticles() {
                    for (var i = 0; i < emitSpeed; i++) {
                        var _dir = Math.random() * aDiff + startAngle;
                        var _spd = Math.random() * speedVariation + spdStart;
                        var _acc = { x: Math.random() * accelerationVariation.x - accStart.x, y: Math.random() * accelerationVariation.y - accStart.y };
                        var spd = { x: Math.cos(_dir) * _spd, y: Math.sin(_dir) * _spd };
                        var posDir = _dir; //Math.random() * Math.PI * 2;
                        var _dis = Math.random() * radius;
                        var _pos = { x: Math.cos(posDir) * _dis + pos.x, y: Math.sin(posDir) * _dis + pos.y };
                        var _life = Math.max(Math.round(Math.random() * lifeVariation + life - lifeVariation / 2), 1);
                        var _p = new Particle(_pos, spd, _acc, alpha, alphaChange, _life, size, sizeChange);
                        particleList.push(_p);
                    }
                }
                function RemoveParticle(index) {
                    particleList = particleList.slice(index);
                }
                this.updatePosition = function (position) {
                    pos = position;
                }
            }
            function Particle(position, speed, acceleration, alpha, alphaChange, life, size, sizeChange) {
                var _pos = position;
                var _speed = speed;
                var _acceleration = acceleration;
                var _alpha = alpha;
                var _alphaChange = alphaChange;
                var _life = life;
                var count = 0;
                var _size = size;
                var _sizeChange = sizeChange;
                this.getAlpha = function () {
                    return _alpha;
                }
                this.getPosition = function () {
                    return _pos;
                }
                this.getSize = function () {
                    return _size;
                }
                this.update = function () {
                    _pos.x += _speed.x;
                    _pos.y += _speed.y;
                    _speed.x += _acceleration.x;
                    _speed.y += _acceleration.y;
                    _alpha += _alphaChange;
                    _alpha = Math.min(Math.max(0, _alpha), 1);
                    _size += _sizeChange;
                    _size = Math.max(0, _size);
                    count++;
                    if (count == _life) return true;
                    else return false;
                }
            }
        }
    });

