'use strict';

// in case there are other version of jquery being imported with this
// version, we will use no conflict and IIFE to avoid global name space
var jq183 = jQuery.noConflict(true);

(function(jq183) {

	jq183(document).ready(function() {
		var alertKeyStartConstant = 'doNotShowAlert';

		var alertArticleKey =
			alertKeyStartConstant + '_' +
			'1185688_' +
			'2020/05/02 21:25:00';

		var alertExpirationDate = new Date('2020/05/02 21:25:00');

		// manually check for the alert expire time, if the alert expire
		// after now, we remove this item from local storage from user
		for (var key in localStorage) {
			// we only remove key when this key starts with the constant
			if (key.indexOf(alertKeyStartConstant) === 0) {
				var alertExpireAt = new Date(key.split("_")[2]);

				if (alertExpireAt.getTime() < new Date()) {
					window.localStorage.removeItem(key);
				}
			}
		}

		// show alert modal to user
		if (!window.localStorage.getItem(alertArticleKey)) {
			jq183.magnificPopup.open({
				items: {
					src: '#homepage_news_alert_modal',
					type: 'inline',
					showCloseBtn: false,
					closeBtnInside: false
				}
			});
		}

		// when user click on `don't show again`, we store this answer
		// in the localStorage
		jq183('.disable').click( function() {
			window.localStorage.setItem(alertArticleKey, true);
		});
	});

})(jq183);

div.script:
(function($JssorSlider$) {
	'use strict';

	var maxWidthOrHeight = 
		document.getElementById('edlio_slider_container_10901').clientWidth;

	var maxNumberOfBullets = Math.round(
		maxWidthOrHeight / 35
	);
	var numberOfSlides = 26;

	var options = {
		$FillMode: 2,             
		$AutoPlay: true,          
		$LazyLoading: 1,          
		$AutoPlayInterval: 3000,  
		$SlideDuration: 500,      
		$PauseOnHover: 0,         
		$DragOrientation: 0,      
		$HWA: false,              
		$SlideshowOptions: {
			$Class: $JssorSlideshowRunner$,
			$Transitions: [
				{$Duration:600,x:-1,$Easing:$JssorEasing$.$EaseInQuad} // swipe left 

			],
			$TransitionsOrder: 0  
		},

		$ArrowNavigatorOptions: {                       
			$Class: $JssorArrowNavigator$,              
			$ChanceToShow: 2,                           
			$AutoCenter: 2,                             
			$Steps: 1                                   
		},
		$ThumbnailNavigatorOptions: {                   
			$Class: $JssorThumbnailNavigator$,          
			$ChanceToShow: 2,                           
			$ActionMode: 1,                             
			$SpacingX: 8,                               
			$DisplayPieces: 8,                          
			$ParkingPosition: 360,                      
			$Orientation: 
					1

		},
		$BulletNavigatorOptions: {                      
			$Class: $JssorBulletNavigator$,             
			$ChanceToShow: 2,                           
			$AutoCenter: 
					1
				,                                
			$Steps: 1,                                  
			$Lanes: Math.ceil(numberOfSlides / maxNumberOfBullets),              
			$SpacingX: 10,                              
			$SpacingY: 10,                              
			$Orientation: 
					1

		},
		$CaptionSliderOptions: {
			$Class: $JssorCaptionSlider$,
			$CaptionTransitions: [{$Duration:700,$Opacity:2,$Brother:{$Duration:1000,$Opacity:2}}],
			$PlayInMode: 1,
			$PlayOutMode: 3
		}

	};
	var edlioJssorSlider = new $JssorSlider$('edlio_slider_container_10901', options);


	function resizeEdlioSlider () {
		var parentWidth = document.getElementById('edlio_slider_container_10901').parentElement.clientWidth;

		if (parentWidth) {
			edlioJssorSlider.$ScaleWidth(parentWidth);
		} else {
			window.setTimeout(resizeEdlioSlider, 30);
		}
	}


		resizeEdlioSlider();







		window.addEventListener("load", resizeEdlioSlider);
		window.addEventListener("resize", resizeEdlioSlider);
		window.addEventListener("orientationchange", resizeEdlioSlider);


})($JssorSlider$);

