div.script:
    var headerTop = $("header").offset().top;
    function headerScrolled() {
        if (window.innerWidth > 991) {
            if ($(window).scrollTop() > headerTop + 56) {
                if ($(".__scrolled_nav_items__").length == 0) {
                    $.each($(".UpperHeader a"), function (index, value) {
                        if (!$(value).hasClass("SearchButtonOpener")) {
                            var liElement = document.createElement("li");
                            $(liElement).addClass("__scrolled_nav_items__");
                            $(value).clone().appendTo(liElement);
                            $(liElement).appendTo("#navigation");
                        }
                    });
                    $("header").css("box-shadow", "0 -10px 5px 10px rgba(0, 0, 0, 0.3)");
                    $(".headerLogoContainer").removeClass("col-md-4");
                    $(".headerLogoContainer").addClass("col-md-1");
                    $(".headerNavContainer").removeClass("col-md-7");
                    $(".headerNavContainer").removeClass("col-md-offset-1");
                    $(".headerNavContainer").addClass("col-md-11");
                    $("header .HeaderLogo").html('Asset 2@100');
                    $("header .HeaderLogo svg").css("max-width", "50px");
                    $("header").css("padding", "15px 0");
                }
                $("header").css("position", "");
            }
            else if ($(window).scrollTop() <= headerTop) {
                if ($(".__scrolled_nav_items__").length > 0) {
                    $("header").css("box-shadow", "");
                    $(".headerLogoContainer").removeClass("col-md-1");
                    $(".headerLogoContainer").addClass("col-md-4");
                    $(".headerNavContainer").removeClass("col-md-11");
                    $(".headerNavContainer").addClass("col-md-offset-1");
                    $(".headerNavContainer").addClass("col-md-7");
                    $(".__scrolled_nav_items__").remove();
                    $("header .HeaderLogo").html('Wordmark@100');
                    $("header .HeaderLogo svg").css("max-width", "");
                    $("header").css("padding", "");
                }
                $("header").css("position", "static");
            }
            else {
                $("header").css("position", "static");
            }
        }
    }
    $(window).scroll(headerScrolled);
    headerScrolled();
    if (!window.hasOwnProperty('webkitSpeechRecognition')) {
        $(".GoogleSearch span").remove();
        $(".GoogleSearch input").css("width", "calc(100% - 30px)");
    }
    function startDictation() {

        if (window.hasOwnProperty('webkitSpeechRecognition')) {

            var recognition = new webkitSpeechRecognition();

            recognition.continuous = false;
            recognition.interimResults = false;

            recognition.lang = "en-US";
            recognition.start();

            $(".header_search_box")[0].placeholder = "Listening...";
            $(".header_search_box")[1].placeholder = "Listening...";
            recognition.onresult = function (e) {
                $(".header_search_box")[0].placeholder = "";
                $(".header_search_box")[0].value = e.results[0][0].transcript;
                $(".header_search_box")[1].placeholder = "";
                $(".header_search_box")[1].value = e.results[0][0].transcript;
                recognition.stop();
                $(".header_search_button")[0].click();
            };

            recognition.onerror = function (e) {
                recognition.stop();
            }

            recognition.onend = function (e) {
                $(".header_search_box")[0].placeholder = "Search...";
                $(".header_search_box")[1].placeholder = "Search...";
            }
        }
    }
    $("body").css("transition", "filter 10s, -webkit-filter 10s");
    $("body").css("-webkit-transition", "filter 10s, -webkit-filter 10s");
