		$('.socialTabs ul li .tab-header').each(function(){
		$(this).click(function(){
		$('.socialTabs ul li .tab-header').removeClass('active-tab');
		$(this).addClass('active-tab');
		$('.socialTabs ul li .social-container').removeClass('active-tab');
		$(this).siblings('.social-container').addClass('active-tab');
		if ($(this).attr('id') == "facebook-container") {
		/* Facebook responsivness */
		if ($(".facebook-container").length) {
		var new_width = $(".facebook-container .social-frame").width();
		$(".facebook-container .social-frame iframe").css("width", new_width);
		var url = $('.facebook-container .social-frame iframe').attr('data-src').split("&width=");
		url = url[0] + '&width=' + new_width + '&height=500&small_header=true&adapt_container_width=true&hide_cover=true&show_facepile=true&appId';
		$('.facebook-container .social-frame iframe').attr('src', url);
		}
		}
		});
		});


