div.script:
$(document).ready(function () {
    $("#ffc358c6-0a7b-4ad0-9d76-e6e554603152").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 6000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#30784605-69a7-43b4-bcaf-b48f8ace7a1f").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#30784605-69a7-43b4-bcaf-b48f8ace7a1f').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#30784605-69a7-43b4-bcaf-b48f8ace7a1f').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 10000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#2f6da8ee-8f3a-4fd0-b40b-27e54af5b5b7').hide();
    jQuery('#2f6da8ee-8f3a-4fd0-b40b-27e54af5b5b7').listSolrResults({
        currentPagePath: '/content/schools/ohva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '2f6da8ee-8f3a-4fd0-b40b-27e54af5b5b7',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

