function popUp(URL) {
        day = new Date();
        id = day.getTime();
        eval("page" + id + " = window.open(URL, '" + id + "', 'toolbar=0,scrollbars=1,location=0,statusbar=1,menubar=0,resizable=1,width=700,height=600,left = 440,top = 212');");
    }

    var community = document.createElement("li");
    $(community).html("Community");
    var parents = document.createElement("li");
    $(parents).html("Parents");
    var students = document.createElement("li");
    $(students).html("Students");
    
    var ul1 = document.createElement("ul");
    var ul2 = document.createElement("ul");
    var ul3 = document.createElement("ul");

    $.each($(".HeaderHover:first-child .Hidden a"), function (i, val) {
        var li = document.createElement("li");
        $(val).clone().appendTo(li).appendTo(ul1);
    });
    $.each($(".HeaderHover:nth-child(2) .Hidden a"), function (i, val) {
        var li = document.createElement("li");
        $(val).clone().appendTo(li).appendTo(ul2);
    });
    $.each($(".HeaderHover:last-child .Hidden a"), function (i, val) {
        var li = document.createElement("li");
        $(val).clone().appendTo(li).appendTo(ul3);
    });
    $(ul1).appendTo(community);
    $(ul2).appendTo(parents);
    $(ul3).appendTo(students);

    $(community).appendTo("#navigation");
    $(parents).appendTo("#navigation");
    $(students).appendTo("#navigation");

    
    $('#navigation').slicknav({
        label: 'Menu',
        prependTo: '#mobile_top_nav',
        allowParentLinks: true,
    });
    
    $(community).remove();
    $(parents).remove();
    $(students).remove();

