$(document).ready(function () {
    $(document).ready(function () {
        $(".hpVideo").click(function () {
            $(".hpVideoContainer").addClass("Clicked");
        });
        $(document).bind('click', function (e) {
            if (!$(e.target).is(".hpVideo")) {
                $(".hpVideoContainer").removeClass("Clicked");
            }
        });
    });
    videoPlacer()
});
$(window).scroll(function () {
    videoPlacer()
});
function videoPlacer() {
    var h = $(".hpVideoContainer").outerHeight();
    var w = $(".hpVideoContainer").outerWidth();
    var t = $(".hpVideoContainer").offset().top - $(window).scrollTop();
    var l = $(".hpVideoContainer").offset().left
    $(".hpVideoContainer iframe").css("width", w + "px");
    $(".hpVideoContainer iframe").css("height", h + "px");
}
setInterval(function () {
    videoPlacer();
}, 1);
