div.script:
$(document).ready(function () {
    $("#bbb1ae92-6a66-4b4f-8332-286309c06265").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#e0adf473-adb8-4f70-8c7b-ca01d69d6853").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#e0adf473-adb8-4f70-8c7b-ca01d69d6853').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#e0adf473-adb8-4f70-8c7b-ca01d69d6853').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 7 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#10864560-bfef-4b37-8e34-ab8d50c5c857').hide();
    jQuery('#10864560-bfef-4b37-8e34-ab8d50c5c857').listSolrResults({
        currentPagePath: '/content/schools/isok/en',
        label: 'Upcoming Events',
        listFrom: 'solr',
        limit: '5',
        listType: 'event',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/content/schools/isok/en/calendar.html',
        viewAllTitle: 'View All',
        uuid: '10864560-bfef-4b37-8e34-ab8d50c5c857',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

