    var CreativeTemplate = {
    	// PROPERTIES
        "DistrictAlias": "hsd",
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ChannelBarIsSticky": false,
        "SlideshowDescriptionIsFixed": false,
        "PasskeysIsMoved": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var template = this;

            this.SetTemplateProps();
            this.MyStart();
            this.Header();
            this.SchoolList();
            this.ChannelBar();
            this.StickyChannelBar();
            this.Body();
            this.ModEvents();
            this.TabbedApps();
            this.Footer();
            this.GlobalIcons();
            this.Slideshow();
            this.CheckSlideshow();
            this.SponsorRotator();
            this.AppAccordion();
            this.Search();
            this.JsMediaQueries();

            $(window).load(function(){ template.WindowLoad(); });
            $(window).resize(function(){ template.WindowResize(); template.StickyChannelBar(); template.CheckSlideshow(); });
            $(window).scroll(function(){ template.StickyChannelBar(); template.CheckSlideshow(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.CalendarListView();

            // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
					if(this.PasskeysIsMoved) {
                    	$("#gb-header-mystart").append($("#sw-mystart-mypasskey"));

                        this.PasskeysIsMoved = false;
                    }
                break;
                case "768":
					if(!this.PasskeysIsMoved) {
                    	$("#gb-channel-list").append($("#sw-mystart-mypasskey"));

                        this.PasskeysIsMoved = true;
                    }
                break;
                case "640":
					if(!this.PasskeysIsMoved) {
                    	$("#gb-channel-list").append($("#sw-mystart-mypasskey"));

                        this.PasskeysIsMoved = true;
                    }
                break;
                case "480":
					if(!this.PasskeysIsMoved) {
                    	$("#gb-channel-list").append($("#sw-mystart-mypasskey"));

                        this.PasskeysIsMoved = true;
                    }
                break;
                case "320":
					if(!this.PasskeysIsMoved) {
                    	$("#gb-channel-list").append($("#sw-mystart-mypasskey"));

                        this.PasskeysIsMoved = true;
                    }
                break;
            }
        },

