            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(document).on("keydown", ".cs-mystart-dropdown .cs-selector", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();
                        var thisLabel = $(this).attr("aria-label");

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(".cs-mystart-dropdown").hasClass("open")){
                        	thisLabel = thisLabel.replace("Collapse", "Expand");
                        	$("+ .cs-dropdown .cs-dropdown-list a").attr("tabindex", "-1");
                            $(this).attr({"aria-expanded": "false", "aria-label": thisLabel}).parent().removeClass("open").find(".cs-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	thisLabel = thisLabel.replace("Expand", "Collapse");
                            $(this).attr({"aria-expanded": "true", "aria-label": thisLabel}).parent().addClass("open").find(".cs-dropdown").attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(".cs-dropdown-list li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }

                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        $("+ .cs-dropdown .cs-dropdown-list a").attr("tabindex", "-1");
                        $(this).attr("aria-expanded", "false").parent().removeClass("open").find(".cs-dropdown").attr("aria-hidden", "true").slideUp(300, "swing");
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ .cs-dropdown .cs-dropdown-list a").attr("tabindex", "-1");
                        $("+ .cs-dropdown .cs-dropdown-list li:first-child > a", this).attr("tabindex", "0").focus();

                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(document).on("keydown", ".cs-mystart-dropdown li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(".cs-dropdown-list").find("a").attr("tabindex", "-1");
                            $(this).closest(".cs-mystart-dropdown").find(".cs-selector").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(".cs-dropdown-list").find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(".cs-dropdown-list").find("a").attr("tabindex", "-1");
                            $(this).closest(".cs-dropdown-list").find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(".cs-dropdown-list").find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	e.preventDefault();

                    	if(e.shiftKey) {
                        	// IS FIRST ITEM
                            if($(this).parent().is(":first-child")) {
                                // FOCUS DROPDOWN BUTTON
                                $(this).closest(".cs-dropdown-list").find("a").attr("tabindex", "-1");
                                $(this).closest(".cs-mystart-dropdown").find(".cs-selector").focus();
                            } else {
                                // FOCUS PREVIOUS ITEM
                                $(this).closest(".cs-dropdown-list").find("a").attr("tabindex", "-1");
                                $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                            }
                        } else {
                        	// IS LAST ITEM
                            if($(this).parent().is(":last-child")) {
                                // FOCUS FIRST ITEM
                                $(this).closest(".cs-dropdown-list").find("a").attr("tabindex", "-1");
                                $(this).closest(".cs-dropdown-list").find("li:first-child > a").attr("tabindex", "0").focus();
                            } else {
                                // FOCUS NEXT ITEM
                                $(this).closest(".cs-dropdown-list").find("a").attr("tabindex", "-1");
                                $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                            }
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(".cs-dropdown-list").find("a").attr("tabindex", "-1");
                        $(this).closest(".cs-dropdown-list").find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(".cs-dropdown-list").find("a").attr("tabindex", "-1");
                        $(this).closest(".cs-dropdown-list").find("li:last-child > a").attr("tabindex", "0").focus();
                    break;
                }
            });*/
        },

        "DropdownActions": function(params) {
            /* ASSUMES THE FOLLOWING STRUCTURE



User Options




Link



            */

        	// FOR SCOPE
            var _this = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
        	});

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.enter:
                    case _this.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                    	if($("+ " + dropdown + " " + dropdownList + " a").length) {
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        	$(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.down:
                    case _this.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case _this.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case _this.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME ESC KEY
                    case _this.KeyCodes.esc:
                        e.preventDefault();

                        // FOCUS DROPDOWN BUTTON AND CLOSE DROPDOWN
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(dropdownSelector).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    break;
                }
            });

            $(dropdownParent).mouseleave(function(e) {
            	if(e.target.nodeName.toLowerCase() !== "select") {
					$(dropdownList + " a", this).attr("tabindex", "-1");
					$(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                }
            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                    if(!$(thisDropdown).find(":focus").length) {
                        $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
        },

        "Header": function() {
        	// FOR SCOPE
            var template = this

            // SITENAME
            var splitLen = 2;
            var siteName = "Hillsboro School District";
            siteName = siteName.split(" ");
            var siteNameLength = siteName.length;
            if(siteNameLength > 2){
                siteNameEnd = $.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
            } else {
                siteNameEnd = $.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
            }
            siteNameBegin = $.trim(siteName.toString().replace(/,/g, " "));

            // ADD FOOTER SITE NAME
            $(".gb.footer.one").prepend("
" + siteNameBegin + " " + siteNameEnd + "");

            // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/OR02216643/Centricity/Template/GlobalAssets/images///school logos/districtLogo.png");
            if(showLogo) {
            	var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;

                if("hsd" == template.DistrictAlias) {
                	if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                        $("#gb-logo-full").append('').removeClass("hidden");
                    } else {
                        $("#gb-logo-full").append('').removeClass("hidden");
                    }
                } else {
                    if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                        $("#gb-logo-side").append('').removeClass("hidden");
                    } else {
                    	$("#gb-logo-side").append('').removeClass("hidden");
                    }

                    // ADD SITENAME
                    var splitLen = 2;
                    var siteName = "Hillsboro School District";
                    siteName = siteName.split(" ");
                    var siteNameLength = siteName.length;
                    if(siteNameLength > 2){
                        siteNameEnd = $.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                    } else {
                        siteNameEnd = $.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                    }
                    siteNameBegin = $.trim(siteName.toString().replace(/,/g, " "));
                    $("#gb-sitename").prepend("
" + siteNameBegin + " " + siteNameEnd + "").removeClass("hidden");

                    $(".gb-header.left").removeClass("hidden");
                }
            }
        },

        "SchoolList": function() {
        	// FOR SCOPE
            var template = this;

            $.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 894,
                'cache': true,
                'schoolListReady': function(schoolList) {
                    var schools = '', sortedSchools = {};

                    // SORT THE SCHOOL LIST TO TRY TO GET THE EXPECTED TYPES IN THE ORDER WE WANT
                    var expectedNameOrder = ["elementary schools", "middle schools", "high schools", "k-8 schools", "alternative education"];
                    schoolList.sort(function(a, b) {
                    	// WE WANT THE SPECIFIC CATEGORIES ABOVE TO COME FIRST AND IN THE SPECIFIED ORDER
                        // WE'LL CAPTURE THOSE IN THIS FIRST CHECK
                        if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > -1 && expectedNameOrder.indexOf(b.schoolType.toLowerCase()) > -1) {
                    		if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) < expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                                return -1;
                            }
                            if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                                return 1;
                            }
                        }

                        // HERE, THE SCHOOL CATEGORY DOESN'T EXIST IN THE CATEGORY ARRAY ABOVE
                        else {
                        	if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                                return -1;
                            }
                            if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) < expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                                return 1;
                            }
                        }

                        // NEITHER CHECK APPLIES SO WE JUST LEAVE THIS IN IT'S CURRENT POSITION
                        return 0;
                    });

                    // SORT THE SCHOOLS INTO TYPE OBJECTS
                    for(var type in schoolList) {
                        if(schoolList[type].schoolVisible.toLowerCase() == 'true') {
                            var thisType = schoolList[type].schoolType.toLowerCase();

                            // CHECK FOR MULTIPLE COMMA SEPARATED VALUES
                            if(schoolList[type].schoolType.indexOf(",") > -1) {
                                var subTypes = schoolList[type].schoolType.split(",");

                                // LOOP THROUGH THE SUB TYPES
                                for(var subType in subTypes) {
                                    var thisSubType = $.trim(subTypes[subType]).toLowerCase();

                                    // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                                    if(!sortedSchools.hasOwnProperty(thisSubType)) {
                                        // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                                        sortedSchools[thisSubType] = [schoolList[type]];
                                    } else {
                                        // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                                        sortedSchools[thisSubType].push(schoolList[type]);
                                    }
                                }
                            }

                            // SINGLE TYPE VALUE
                            else {
                                // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                                if(!sortedSchools.hasOwnProperty(thisType)) {
                                    // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                                    sortedSchools[thisType] = [schoolList[type]];
                                } else {
                                    // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                                    sortedSchools[thisType].push(schoolList[type]);
                                }
                            }
                        }
                    }

                    // BUILD THE SCHOOL MENU STRUCTURE
                    var schoolMenuLists = '';
                    var schoolNumCounter = 1;
                    $.each(sortedSchools, function(type, schools) {
                        var typeName = type.replace(/\s/g, "-").replace(/&/g, "and");

                        // SORT SCHOOLS ARRAY ALPHABETICALLY IN CASE SCHOOLS GOT SHIFTED A BIT FROM MULTIPLE TYPE VALUES
                        schools.sort(function(a, b) {
                            if(a.schoolName > b.schoolName) {
                                return 1;
                            }
                            if(a.schoolName < b.schoolName) {
                                return -1;
                            }
                            return 0;
                        });

                        // START THE CATEGORY CONTAINER
                        schoolMenuLists += '
';

                        // BUILD HEADER
                        schoolMenuLists += '
' + type + '';

                        // BUILD LIST
                        schoolMenuLists += '';
                        for(var school in schools) {
                            schoolMenuLists += '
' + schools[school].schoolName + '';

                        	schoolNumCounter++;
                        }

                        // CLOSE EVERYTHIGN UP
                        schoolMenuLists += '';
                    });

                    // ADD THE COMPLETED MENU TO THE DOM
                    $("#gb-school-list").html(schoolMenuLists + 'Close');

                    // MAKE SOME CUSTOM ADJUSTMENTS
                    $(".gb-school-list-cat-container:not(.elementary-schools)").wrapAll('
');

                    // BIND THE TAB PANEL EVENTS
                    template.SchoolMenuControls();

                    // BUILD THE RESPONSIVE MENUS NOW THAT WE HAVE THE SCHOOLS
                    template.PageListMenu();
                    template.RsMenu(sortedSchools);
                }
            });
        },

        "SchoolMenuControls": function() {
        	// FOR SCOPE
            var template = this;

        	// SCHOOLS BUTTON CLICK/KEYDOWN
            $(".cs-mystart-button.schools").on("click keydown", "a", function(e) {
                if(template.AllyClick(e)) {
                	e.preventDefault();

                    // MENU IS NOT OPEN
                    if($(".cs-mystart-button.schools a").attr("aria-expanded") == "false") {
                        // OPEN THE MENU
                        $("#gb-school-list-outer").fadeIn().attr("aria-hidden", "false");
                        if(e.type == "keydown") {
                        	$("#gb-school-list .gb-school-list-links.elementary-schools > li:first-child a").focus();
                        }

                        // SET ATTRIBUTES
                        $("body").addClass("school-list-overflow");
                        $(".cs-mystart-button.schools").addClass("open").find("a").attr("aria-expanded", "true");
                    }

                    // MENU IS OPEN
                    else {
                        // CLOSE THE MENU
                        $("#gb-school-list-outer").fadeOut(function() {
                            // SET ATTRIBUTES
                            $("body").removeClass("school-list-overflow");
                        }).attr("aria-hidden", "true");

                        // SET ATTRIBUTES
                        $(".cs-mystart-button.schools").removeClass("open").find("a").attr("aria-expanded", "false");
                    }
                }
            });

            // SHIFT+TAB FROM FIRST LINK
            $(document).on("keydown", "#gb-school-list .gb-school-list-links.elementary-schools > li:first-child a", function(e) {
            	if(e.keyCode == template.KeyCodes.tab && e.shiftKey) {
                    e.preventDefault();

                    $("#gb-close-school-menu").focus();
                }
            });

            // TAB FROM CLOSE BUTTON
            $(document).on("keydown", "#gb-close-school-menu", function(e) {
            	if(e.keyCode == template.KeyCodes.tab && !e.shiftKey) {
                    e.preventDefault();

                    $("#gb-school-list .gb-school-list-links.elementary-schools > li:first-child a").focus();
                }
            });

            // LINK ARROW CONTROLS
            $(document).on("keydown", ".gb-school-list-links a", function(e) {
            	var thisNum = parseInt($(this).attr("data-school-num"));
                var totalNum = $(".gb-school-list-links a").length;

            	switch(e.keyCode) {
                	case template.KeyCodes.up:
                    case template.KeyCodes.left:
                    	e.preventDefault();

						if(thisNum == 1) {
                        	$("#gb-close-school-menu").focus();
                        } else {
                        	$(".gb-school-list-links a[data-school-num='" + (thisNum - 1) + "']").focus();
                        }
                    break;

                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                    	e.preventDefault();

                        if(thisNum == totalNum) {
                        	$("#gb-close-school-menu").focus();
                        } else {
                        	$(".gb-school-list-links a[data-school-num='" + (thisNum + 1) + "']").focus();
                        }
                    break;
                }
            });

            // CLOSE MENU
            $("#gb-school-list-outer").click(function(e) {
            	// CLOSE THE MENU
                $("#gb-school-list-outer").fadeOut(function() {
                    // SET ATTRIBUTES
                    $("body").removeClass("school-list-overflow");
                }).attr("aria-hidden", "true");

                // SET ATTRIBUTES
                $(".cs-mystart-button.schools").removeClass("open").find("a").attr("aria-expanded", "false").focus();
            });
            $("#gb-school-list").click(function(e) {
            	e.stopPropagation();
            });

            $("#gb-school-list").on("click keydown", "#gb-close-school-menu", function(e) {
            	if(template.AllyClick(e)) {
                	e.preventDefault();

                	// CLOSE THE MENU
                    $("#gb-school-list-outer").fadeOut(function() {
                        // SET ATTRIBUTES
                        $("body").removeClass("school-list-overflow");
                    }).attr("aria-hidden", "true");

                    // SET ATTRIBUTES
                    $(".cs-mystart-button.schools").removeClass("open").find("a").attr("aria-expanded", "false").focus();
                }
            });

            // CLOSE MENU WITH ESC
            $(document).on("keydown", "#gb-school-list-outer", function(e) {
                if(e.keyCode == template.KeyCodes.esc) {
                    e.preventDefault();

                    // CLOSE THE MENU
                    $("#gb-school-list-outer").fadeOut(function() {
                        // SET ATTRIBUTES
                        $("body").removeClass("school-list-overflow");
                    }).attr("aria-hidden", "true");

                    // SET ATTRIBUTES
                    $(".cs-mystart-button.schools").removeClass("open").find("a").attr("aria-expanded", "false").focus();
                }
            });

            // FOCUS LEAVES SCHOOL MENU
            $(document).keyup(function() {
                /// IF THIS DROPDOWN OR ANY OF ITS CHILDREN DO NOT HAVE FOCUS
                if(!$(".cs-mystart-button.schools a").is(":focus") && !$("#gb-school-list-outer").find(":focus").length) {
                    // CLOSE THE MENU
                    $("#gb-school-list-outer").fadeOut(function() {
                        // SET ATTRIBUTES
                        $("body").removeClass("school-list-overflow");
                    }).attr("aria-hidden", "true");

                    // SET ATTRIBUTES
                    $(".cs-mystart-button.schools").removeClass("open").find("a").attr("aria-expanded", "false");
                }
            });
        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });
        },

        "StickyChannelBar": function() {
        	// FOR SCOPE
            var template = this;

        	if(!this.IsMyViewPage) {
            	var OnSubpage = $(".sp").length;

                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var gbNavOffSet = $("nav").offset().top;
                        if ($(window).scrollTop() <= gbNavOffSet) {
                            if(this.ChannelBarIsSticky) {
                                $("nav, #gb-channel-list-outer").removeClass("sticky");
                                if(OnSubpage) $("nav").removeAttr("style");

                                this.ChannelBarIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            if(OnSubpage) $("nav").css("height", $("#gb-channel-list-outer").outerHeight(true));

                            if(!this.ChannelBarIsSticky) {
                                $("nav, #gb-channel-list-outer").addClass("sticky");

                                this.ChannelBarIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.ChannelBarIsSticky) {
                            $("nav, #gb-channel-list-outer").removeClass("sticky");
                            if(OnSubpage) $("nav").removeAttr("style");

                            this.ChannelBarIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
            if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
                $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
                //DOC add signed out breadcrumb
                $(".ui-breadcrumbs").append("
Signed Out");
            }

            // ADJUST SIGN IN PAGE
            if($("#swlogin").length){
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
                $(".ui-breadcrumbs").append("
Sign In");
            }

            // ADJUST REGISTER PAGE
            if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
                $(".ui-breadcrumbs").append("
Register");
            }

            // ADJUST SEARCH RESULTS PAGE
            if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
                $("#sw-content-layout-wrapper").children().wrapAll('

');
                $(".ui-breadcrumbs").append("
Search Results");
            }

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // ADJUST MORE LINKS
            $("a.more-link span").text("View All");

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            // CHECK FOR EMPTY HOMEPAGE CONTAINERS
            if(!$(".hp-content.top .ui-widget.app").length) {
            	$(".hp-content.top").addClass("hidden").hide();
            }
            if(!$(".hp-column-two.top .ui-widget.app").length) {
            	$(".hp-column-two.top").addClass("hidden").hide();
            }
            if(!$(".hp-column-two.bottom .ui-widget.app").length) {
                $(".hp-column-two.bottom").addClass("hidden").hide();
            } else {
            	$(".hp-column-two.top").addClass("apps-under");
            }
            if(!$("#sw-content-container4.ui-hp .ui-widget.app").length) {
            	$("#sw-content-container4.ui-hp").addClass("hidden").hide();
            }
            if(!$("#sw-content-container5.ui-hp .ui-widget.app").length) {
            	$("#sw-content-container5.ui-hp").addClass("hidden").hide();
            } else {
            	$("#sw-content-container4.ui-hp").addClass("apps-under");
            }

            // HOMEPAGE CONTENT SCROLL BUTTON
            $("#hp-content-scroll").click(function(e) {
            	e.preventDefault();

                $("body, html").animate({
                	scrollTop: $(".hp-content-outer.top").offset().top - $("#gb-channel-list-outer").outerHeight(true)
                }, 500);
            });

            // MOVE HEADLINES MORE LINK
            $(".ui-widget.app.headlines").each(function() {
            	$(".ui-widget-detail", this).append($(".more-link", this));
                $(".more-link-under", this).remove();
            });

            // MOVE RETURN TO HEADLINES LINK
            $(".ui-widget.app.headlines.detail").each(function() {
            	 $(this).append($("a.ui-return", this));
            });

            // WRAP HEADLINE ARTICLE CONTENT IN HOMEPAGE FEATURE
            $(".hp-content.bottom .ui-widget.app.headlines .ui-article").each(function() {
            	$(this).attr("tabindex", "0").children().not($(".ui-article-thumb", this)).wrapAll('
');
                $(".ui-article-thumb img:not([src])", this).closest(".ui-article").addClass("no-thumb").find(".ui-article-content").attr("aria-hidden", "false");
            });

            // HEADLINE FEATURE EVENTS
            $(".hp-content.bottom .ui-widget.app.headlines").on("focusin touchstart", ".ui-article", function(e) {
            	$(this).addClass("hover").find(".ui-article-content").attr("aria-hidden", "false");
            }).on("focusout touchend", ".ui-article", function(e) {
                $(this).removeClass("hover").find(".ui-article-content").attr("aria-hidden", "true");
            });
        },

        "ModEvents": function() {
            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "no",
                    dayWeek     : "yes"
                });

                $(".upcomingevents .ui-article").each(function(){
                	if(!$(".upcoming-column.right .ui-article-description .sw-calendar-block-time", this).length) {
                    	$(".upcoming-column.right .ui-article-description", this).prepend('ALL DAY');
                    }

                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };

                    $(".upcoming-column.right", this).prepend($(".joel-day", this));
                });
            }

            $(".upcomingevents").removeAttr("style");
            $(".view-calendar-link span").text("View Full Calendar");
        },

