        "Footer": function() {
        	// FOR SCOPE
            var template = this;

        	// REMOVE UNUSED FOOTER LINKS
            $("#gb-footer-links ul li.false, #gb-social-icons li.false").remove();

            // BUILD GOOGLE MAP URL
