        	// FOR SCOPE
            var template = this;

        	$("div.ui-widget.app.gallery.json").creativeGalleryRotate({
                "efficientLoad" : "yes",
                "imageWidth" : 320,
                "imageHeight" : 220,
                "appHeader" : "yes",
                "titles" : "no",
                "captions" : "no",
                "playPause" : "no",
                "mainControls" : "yes",
                "galleryOverlay" : "no",
                "linkedElement" : ["image"], // ["image", "title", "overlay", "all"]
                "contMoreLink" : "no",
                "contMoreLinkText" : "Read More",
                "descMoreLink" : "no",
                "descMoreLinkText" : "Read More",
                "bullets" : "no",
                "bulletControls" : "no",
                "thumbnails" : "no",
                "thumbnailWidth" : 122,
                "thumbnailHeight" : 52,
                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                "hoverPause" : "yes",
                "fullScreenRotator" : "no",
                "fullScreenBreakpoints" : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                "autoRotate" : "yes",
                "transitionType" : "fade", // fade or slide
                "transitionSpeed" : 2,
                "customResize" : "yes",
                "customResizeFunction" : function(element, imgWidth, imgHeight) {
                	// GET THE WIDTHS AND HEIGHTS WE NEED
                    var viewWidth = $(".rs-photo-gallery-images .rs-photo-gallery-images-list li", element).width();

                    // GET IMAGE RATIO
                    var maxWidth = (template.GetBreakPoint() == "320") ? 200 : 320;
                    var initW = imgWidth, initH = imgHeight;
                    if(viewWidth > maxWidth) viewWidth = maxWidth;
                    var ratio = initH / initW;
                    var viewHeight = viewWidth * ratio;

                    // APPLY THE DIMENSIONS
                    $(".rs-photo-gallery-container .rs-photo-gallery-images", element).css("height", viewHeight);
                    $(".rs-photo-gallery-container .rs-photo-gallery-images .rs-photo-gallery-images-list", element).css("height", viewHeight);
                    $(".rs-photo-gallery-container .rs-photo-gallery-images .rs-photo-gallery-images-list li", element).css("height", viewHeight);
                    $(".rs-photo-gallery-container .rs-photo-gallery-images .rs-photo-gallery-images-list li img", element).removeAttr("style");
                },
                "onImageLoad" : function() { },
                "allImagesLoaded" : function() { },
                "onThumbnailLoad" : function() { },
                "allThumbnailsLoaded" : function() { },
                "onTransitionStart" : function() { },
                "onTransitionEnd" : function() { },
                "allLoaded" : function() {
                	$(".rs-photo-gallery-container", this).attr("tabindex", "0");
                    $(".rs-photo-gallery-control", this).attr("aria-hidden", "true");

                    $(".rs-photo-gallery-container", this).mouseenter(function() {
                    	$(".rs-photo-gallery-control", this).attr("aria-hidden", "false");
                    }).mouseleave(function() {
                    	$(".rs-photo-gallery-control", this).attr("aria-hidden", "true");
                    }).focusin(function() {
                    	$(this).addClass("hover");
                        $(".rs-photo-gallery-control", this).attr("aria-hidden", "false");
                    }).focusout(function() {
                    	$(this).removeClass("hover");
                        $(".rs-photo-gallery-control", this).attr("aria-hidden", "true");
                    });
                }
            });
        },

