        "RsMenu": function(sortedSchools) {
        	var additionalItems = {};

            // BUILD THE SCHOOL MENU STRUCTURE
            var schoolItems = [];
            $.each(sortedSchools, function(type, schools) {
            	schoolItems = [];

                // SORT SCHOOLS ARRAY ALPHABETICALLY IN CASE SCHOOLS GOT SHIFTED A BIT FROM MULTIPLE TYPE VALUES
                schools.sort(function(a, b) {
                    if(a.schoolName > b.schoolName) {
                        return 1;
                    }
                    if(a.schoolName < b.schoolName) {
                        return -1;
                    }
                    return 0;
                });

                for(var school in schools) {
                	schoolItems.push({ "text": schools[school].schoolName, "url": schools[school].schoolUrl, "target": '_self;' });
                }

                additionalItems[type] = schoolItems;
            });

            // BUILD THE GLOBAL ICONS
            var globalIcons = [
                ["Contact Us", "/Page/230", "_self"],
                ["Calendar", "[SITECALENDARLINK]", "_self"],
                ["Jobs", "https://hillsboro1j.tedk12.com/hire/index.aspx", "_self"],
                ["Meals", "/nutrition", "_self"],
                ["Bus Info", "/Page/671", "_self"],
                ["Safety", "/domain/89", "_self"],
                ["Transcripts", "/Page/252", "_self"],
                ["Volunteering", "/domain/62", "_self"]
            ];
            var iconNum = ("8" == "8;7;6;5;4;3;2;1;0") ? 0 : "8";
