				for(var i = 0; i < iconNum; i++) {
                	var linkCheck = (globalIcons[i][1].toLowerCase() == "[sitecalendarlink]") ? "/Page/2" : globalIcons[i][1];
                    globalLinks.push({ "text": globalIcons[i][0], "url": linkCheck, "target": globalIcons[i][2] });
                }

                additionalItems["Global Links"] = globalLinks;
            }

