$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=unVl%2f37XS2ezo7N3EN7WNQ%3d%3d&SessionID=1a2da673-914a-4ba5-a995-36067447bb04&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f19%2f2020+10%3a58%3a36+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.1.129';
