$('•ATTENDANCE '+n+'').insertBefore('.'+s+' .gb.footer.one p:eq("2") span.colored-bullet:eq("0")');
$('
ATTENDANCE '+n+'').insertBefore('.'+s+' .gb.footer.one p:eq("5")');
}
