        "Search": function() {
        	var defSearchText = "Search...";

            var searchBox = $('#gb-search-input');

            searchBox
                .attr("placeholder", '')
                .val(defSearchText)
                .focus(function() {
                    if($(this).val() == defSearchText) {
                        $(this).val("");
                    }
                })
                .blur(function() {
                    if($(this).val() == "") {
                        $(this).val(defSearchText);
                    }
                });

            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($("#gb-search-input").val().trim() != "" && $("#gb-search-input").val() != defSearchText) {
                    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
            });

            $("#gb-search-form input").blur(function(e){
            	$('#gb-search-trigger').attr('aria-expanded', 'false');
            	$('#gb-search-form').attr('aria-hidden', 'true');
            });

            $('#gb-search-trigger').click(function(e){
            	if( $(this).attr('aria-expanded') == "false" ){
                	$(this).attr('aria-expanded', 'true');
                    $('#gb-search-form').attr('aria-hidden', 'false');
                }else{
                	$(this).attr('aria-expanded', 'false');
                    $('#gb-search-form').attr('aria-hidden', 'true');
                }
            })
            .keydown(function(e){
            	if(e.keyCode == 13 || e.keyCode == 32){
                	setTimeout(function(){
                    	$('#gb-search-form input').focus();
                    }, 300);
                }
            });
        },
        "SmoothScroll": function(to, offset){
        	/*
            ** smoothScroll
            ** Use: This will smoothly scroll to the id that was passed to the function.
            ** Return: N/A
            ** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
            ** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
            */
        	if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
            var target = $(to);

            if (target.length) {
                $('html,body').animate({
                    scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
                }, 800, function(){
                    $("html, body").stop(true, false);
                });
                return false;
            }
        },
        "StickyHeader": function() {
            var getWidth = $(window).width();
            if (getWidth >= 1024) {
                // CHANNEL BAR
                var gbNavOffSet = $("#gb-sticky-header-wrapper").offset().top + 220;

                if ($(window).scrollTop() <= gbNavOffSet) {
                    if(CreativeTemplate.csNavIsSticky) {  
                    	$("#gb-sticky-header-wrapper").css("height", "");
                    	$("#gb-sticky-header-wrapper, #gb-page").removeClass("sticky-on");
                        CreativeTemplate.csNavIsSticky = 0;
                    }
                } else {

                    if(!CreativeTemplate.csNavIsSticky) {
						$("#gb-sticky-header-wrapper").css("height", $("#gb-sticky-header").outerHeight(true));
                        $("#gb-sticky-header-wrapper, #gb-page").addClass("sticky-on");
                        CreativeTemplate.csNavIsSticky = 1;
                    }

                    if($(".sp").length && $(".ui-widget.app.calendar").length) {
                        if($(".wcm-controls").hasClass("wcm-stuck")) {
                            $(".wcm-controls").css("margin-top", $("#gb-sticky-header").outerHeight(true));
                        }
                    }
                }

                $("#sw-maincontent").css({
                    "display": "block",
                    "position": "relative",
                    "top": "-" + (($("#gb-sticky-header-wrapper").outerHeight(true) > 150) ? $("#gb-sticky-header-wrapper").outerHeight(true) : 175) + "px"
                });
            } else {
                if(CreativeTemplate.csNavIsSticky == 1) {
                    $("#gb-sticky-header-wrapper, #gb-page").removeClass("sticky-on");
                    CreativeTemplate.csNavIsSticky = 0;
                }
                $("#gb-sticky-header-wrapper").css("height", '');

                $("#sw-maincontent").removeAttr("style");
            }
        },
        "MoveFooterLinks": function(area){
        	if( $('.hp').length > 0 ){
                if(area == "footer"){
					$('.ui-widget.app').each(function(appIndex, app){
                        if( $('.view-calendar-link', app).length || $('.more-link', app).length ){
                            $('.ui-widget-footer', app).append( (($('.view-calendar-link', app).length) ? $('.view-calendar-link', app) : $('.more-link', app)) );
                        }
