                }else{
                    $('.ui-widget.app').each(function(appIndex, app){
                        if( $('.view-calendar-link', app).length || $('.more-link', app).length ){
                            $('.ui-widget-header', app).append( (($('.view-calendar-link', app).length) ? $('.view-calendar-link', app) : $('.more-link', app)) ).addClass('flex-cont jc-sb ai-c');
                        }
                    });
                }
			}
        },


