                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                });

                $(".upcomingevents .ui-article").each(function(articleIndex, article){
					var oldHtmlSplit = $('.sw-calendar-block-date', article).html().split('');
                    $('.sw-calendar-block-date', article).html(oldHtmlSplit[1] + '' + oldHtmlSplit[2] + '' + oldHtmlSplit[0] + '');
                });
            }
        }
