        //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.stop(true,true).slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

        //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
        $(".sw-mystart-dropdown").unbind('hover');
        $(".sw-mystart-dropdown").hover(function(e) {
            e.preventDefault();
        }, function() {
            $(".sw-dropdown-arrow",this).removeClass("rotate-arrow");
            //$(".sw-dropdown .sw-dropdown-list li a",this).focusout();
            $(".sw-dropdown",this).slideUp(300, "swing").attr('aria-hidden', 'true');
            $(this).removeClass("open");
        });
        $(document).off("focus", ".sw-mystart-dropdown");
        $(document).on("focus", ".sw-mystart-dropdown", function(e) {
            $(".sw-dropdown", this).slideDown(300, "swing").attr('aria-hidden', 'false');
            $(this).addClass("open");
        });
        $(".sw-mystart-dropdown").focusout(function() {
            //$(".sw-dropdown", this).slideUp(300, "swing").attr('aria-hidden', 'true');
            $(this).removeClass("open");
        });

        $("#sw-mystart-account").unbind('hover');
        $("#sw-mystart-account").hover(function(e) {
            e.preventDefault();
        }, function() {
            $(".sw-dropdown-arrow",this).removeClass("rotate-arrow");
            //$(".sw-dropdown .sw-dropdown-list li a",this).focusout();
            $("#sw-myaccount-list",this).slideUp(300, "swing").attr('aria-hidden', 'true');
            $(this).removeClass("open");
        });
        $(document).off("focus", "#sw-mystart-account");
        $(document).on("focus", "#sw-mystart-account", function(e) {
            $("#sw-myaccount-list", this).slideDown(300, "swing").attr('aria-hidden', 'false');
            $(this).addClass("open");
        });
        $("#sw-myaccount-list").focusout(function() {
            //$(".sw-dropdown", this).slideUp(300, "swing").attr('aria-hidden', 'true');
            $(this).removeClass("open");
        });

		if($(window).width() >= 1024) {
			//headlineHeight();
			eventsHeight();
		}

		$("#footer-map").css("display","inline-block");

		if($("#hp-slideshow .rs-gallery-images-list li.image.active .watch-btn").length) {
			$("#hp-slideshow .rs-gallery-container > .watch-btn").show();
		}

		var sliderLine = $("#channel-slider");
		if($("li.sw-channel-item.active").size()) {
			sliderLine.width($("li.sw-channel-item.active").width()).css("left", $("li.sw-channel-item.active").position().left + 13).data("origLeft", sliderLine.position().left).data("origWidth", sliderLine.width());
		} else {
			sliderLine.width($("li.sw-channel-item").eq(0).width()).css("left", $("li.sw-channel-item").eq(0).position().left + 13).data("origLeft", sliderLine.position().left).data("origWidth", sliderLine.width());	
		}

        $(".sw-skipnav-outerbar").prependTo("#gb-header-outer");
        $("#sw-maincontent").css({
            "display": "block",
            "position": "relative",
            "top": "-" + $("#gb-header-outer").outerHeight(true) + "px"
        });

        megaMenu();
	});

	$(window).scroll(function() {
		if($(".hp").length) {
			var scrollTop = $(window).scrollTop();
			var scrollParallax = $("#hp-content-outer").offset().top

			if(scrollTop > scrollParallax) {
				parallax();
			}
		}
	});

	function parallax() {
		if($(window).width() >= 768) {
			var scrolled = $(window).scrollTop() / 5;
		  	$('#hp-content-four-bg ').css('top',-(scrolled*0.5)+'px');
		}  
	}

	function headlineHeight() {
		//GET HEIGHT OF EACH ARTICLE IN CONT. 4 ON HP
        	var maxHeight = -1;
		$('.hp.column.one .ui-articles li').each(function() {
			maxHeight = maxHeight > $(this).height() ? maxHeight : $(this).height();
		});
		//SET LARGEST HEIGHT ON EACH ARTICLE IN CONT. 4 ON HP
		$('.hp.column.one .ui-articles li').each(function() {
			$(this).height(maxHeight + 20);
		});
	}

	function eventsHeight() {
		//GET HEIGHT OF EACH ARTICLE IN CONT. 4 ON HP
        	var maxHeight = -1;
		$('#hp-content-three .ui-articles li').each(function() {
			maxHeight = maxHeight > $(this).height() ? maxHeight : $(this).height();
		});
		//SET LARGEST HEIGHT ON EACH ARTICLE IN CONT. 4 ON HP
		$('#hp-content-three.ui-articles li').each(function() {
			$(this).height(maxHeight + 20);
		});
	}

	$(function(){


		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE7){ $("body").addClass("msie7"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }
		if(isMSIE11){ $("body").addClass("msie11"); }
		$("body").addClass(navigator.platform + " " + browserName + " " + browserName + browserVersion);

		$(window).bind("resize", function(){  
			pgScreenSize();
			if($(window).width() >= 320 && $(window).width() <= 639){
				calendarListView();
			}
		});

		var socialIcons = [
			["Facebook", "true", "https://www.facebook.com/CheltenhamSD"],
			["Twitter", "true", "https://twitter.com/cheltenhamsd"],
			["YouTube", "false", "#"],
			["Instagram", "false", "#http://ink361.com/app/users/ig-1463341524/drmarseillelmsd/photos"],
			["Flickr", "false", "#"],
			["Pinterest", "false", "#"],
			["Vimeo", "false", "#"]
		];

		var extraItems = "";
		for(var s in socialIcons) {
			if(socialIcons[s][1] == "true") {
				extraItems += "1, " + socialIcons[s][0] + ", " + socialIcons[s][2] + ", _self;";
			}
		}

		var additionalSocialIcons = [
			["", "http://www.gmail.com"],
			["Calendar", "https://www.cheltenham.org/Page/2"],
			["", "#"]
		];

		var iconNum = ('2' == "0;1;2;3") ? 0 : '2';
		for(var i = 0; i < iconNum; i++) {
