	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			appViewReset();
			//var headerHeight = $("#gb-header-outer").height();
			//$("#cs-content-container").css("margin-top",headerHeight+"px");
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			appView768();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			appView768();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			appView();
		}
	}

	function socialIconSlider() {
		$("#gb-footer .gb-footer-social h2").click(function() {
			$(this).toggleClass("rotate-arrow");
			if($(this).hasClass("rotate-arrow")) {
				$(".social-icon-cont").slideDown();
			} else {
				$(".social-icon-cont").slideUp();
			}
		});
	}

