	function appView() {
		if(!appViewSet) {
			// MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
			/*if(!$("#hp-content-outer div.ui-widget.app.active").length){
				$("#hp-content-one div.ui-widget.app:first").addClass("active");
			}*/

			// DO NOT ADD APP INTO ACCORDION IF IT DOES NOT HAVE A HEADER
			$("#hp-content div.ui-widget.app").each(function() {
				if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
					$(this).addClass("no-header");
				}
			});

			// ACCORDION HEADER CLICK
			$("#hp-content-outer div.ui-widget-header").not("#hp-content-outer #hp-announcements div.ui-widget-header").click(function(){
				var clickedApp = $(this).parent().parent().attr("id");

				$("#hp-content-outer div.ui-widget.app").removeClass("active");
				$(this).parent().addClass("active");

				$("html, body").animate({
					scrollTop: $("#" + clickedApp).offset().top
				}, 500, function(){
					$("html, body").stop(true, false);
				});
			});

			appViewSet = 1;
		}
	}

	function appView768() {
		if(!appViewSet) {
			// MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
			if(!$(".hp.column.one div.ui-widget.app.active").length){
				$(".hp.column.one div.ui-widget.app:first").addClass("active");
			}

			// DO NOT ADD APP INTO ACCORDION IF IT DOES NOT HAVE A HEADER
			$(".hp.column.one div.ui-widget.app").each(function() {
				if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
					$(this).addClass("no-header");
				}
			});

			// ACCORDION HEADER CLICK
			$(".hp.column.one div.ui-widget-header").click(function(){
				var clickedApp = $(this).parent().parent().attr("id");

				$(".hp.column.one div.ui-widget.app").removeClass("active");
				$(this).parent().addClass("active");

				$("html, body").animate({
					scrollTop: $("#" + clickedApp).offset().top
				}, 500, function(){
					$("html, body").stop(true, false);
				});
			});

			appViewSet = 1;
		}
	}

	function appViewReset() {
		if(appViewSet) {
			$("#hp-content div.ui-widget-header").unbind("click");

			appViewSet = 0;
		}
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }

    function megaMenu(){
            //DOC Mega Menu
            var megaMenuData = [{
            	"channelSwitch": false,
                "channelImg": "",
                "channelImgSwitch": "false",
                "channelCaption": "",
                "channelColumnOneTitle": "",
                "channelColumnOneSections": "",
                "channelColumnTwoTitle": "",
                "channelColumnTwoSections": "",
                "channelColumnThreeTitle": "",
                "channelColumnThreeSections": ""
            },{
            	"channelSwitch": false,
                "channelImg": "",
				"channelImgSwitch": "false",
                "channelCaption": "",
                "channelColumnOneTitle": "",
                "channelColumnOneSections": "",
                "channelColumnTwoTitle": "",
                "channelColumnTwoSections": "",
                "channelColumnThreeTitle": "",
                "channelColumnThreeSections": ""
            },{
            	"channelSwitch": false,
                "channelImg": "",
                "channelImgSwitch": "false",
                "channelCaption": "",
                "channelColumnOneTitle": "",
                "channelColumnOneSections": "",
                "channelColumnTwoTitle": "",
                "channelColumnTwoSections": "",
                "channelColumnThreeTitle": "",
                "channelColumnThreeSections": ""
            },{
            	"channelSwitch": false,
                "channelImg": "",
                "channelImgSwitch": "false",
                "channelCaption": "",
                "channelColumnOneTitle": "",
                "channelColumnOneSections": "",
                "channelColumnTwoTitle": "",
                "channelColumnTwoSections": "",
                "channelColumnThreeTitle": "",
                "channelColumnThreeSections": ""
            },{
            	"channelSwitch": false,
                "channelImg": "",
                "channelImgSwitch": "false",
                "channelCaption": "",
                "channelColumnOneTitle": "",
                "channelColumnOneSections": "",
                "channelColumnTwoTitle": "",
                "channelColumnTwoSections": "",
                "channelColumnThreeTitle": "",
                "channelColumnThreeSections": ""
            },{
            	"channelSwitch": true,
                "channelImg": "/cms/lib/PA01916448/Centricity/template/globalassets/images/channel-images/default-channel-image.png",
                "channelImgSwitch": "true",
                "channelCaption": "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer iaculis feugiat fermentum. Fusce tempus elit non tincidunt dictum.",
                "channelColumnOneTitle": "Optional category title",
                "channelColumnOneSections": "5",
                "channelColumnTwoTitle": "Optional category title",
                "channelColumnTwoSections": "4",
                "channelColumnThreeTitle": "Optional category title",
                "channelColumnThreeSections": "4"
            }];

            // BUILD THE MEGA MENUS
            $("#sw-channel-list-container li.sw-channel-item").each(function(index){
            	var index = index;
                if($("#sw-channel-list-container li.sw-channel-item.home").length) {
                	var i = $(this).index();
                } else {
                	var i = $(this).index() - 1;
                }
				var channelText = $(".sw-channel-item > a > span").text();

                //IGNORE HOME AND CALENDAR CHANNELS
                if(channelText != "home" && channelText != "calendar" && $("ul.sw-channel-dropdown li", this).length && megaMenuData[i].channelSwitch) {
                    // SET THE CHANNELS INDEX
                    var imgSrc = jQuery.trim(megaMenuData[i].channelImg);
                    var imgSwitch = megaMenuData[i].channelImgSwitch;
                    var srcCheck = imgSrc.indexOf("Faces/default-man.jpg") > -1;                 

                    if((megaMenuData[i].channelColumnOneSections != 0) || (megaMenuData[i].channelColumnTwoSections != 0) || (megaMenuData[i].channelColumnThreeSections != 0)) {
                    	var sectionCount = 0;
                       	$(".sw-channel-dropdown", this).append('
');
                        if(megaMenuData[i].channelColumnOneSections != 0) {
                        	$(".sw-channel-dropdown .cs-mega-menu-sections > ul", this).append('
');
                            $(".sw-channel-dropdown > li:not(.cs-mega-menu-info, .cs-mega-menu-sections, .cs-mega-menu-section)", this).slice(0, megaMenuData[i].channelColumnOneSections).appendTo($(".cs-mega-menu-section-one > ul", this));
                            if(jQuery.trim(megaMenuData[i].channelColumnOneTitle) != "") {
                            	$(".cs-mega-menu-section-one ul", this).prepend('
'+jQuery.trim(megaMenuData[i].channelColumnOneTitle)+'');
                            }
                        	sectionCount++;
                   		}
                        if(megaMenuData[i].channelColumnTwoSections != 0) {
                        	$(".sw-channel-dropdown .cs-mega-menu-sections > ul", this).append('
');
                            $(".sw-channel-dropdown > li:not(.cs-mega-menu-info, .cs-mega-menu-sections, .cs-mega-menu-section)", this).slice(0, megaMenuData[i].channelColumnTwoSections).appendTo($(".cs-mega-menu-section-two > ul", this));
                            if(jQuery.trim(megaMenuData[i].channelColumnTwoTitle) != "") {
                            	$(".cs-mega-menu-section-two ul", this).prepend('
'+jQuery.trim(megaMenuData[i].channelColumnTwoTitle)+'');
                            }
                        	sectionCount++;
                   		}
                        if(megaMenuData[i].channelColumnThreeSections != 0) {
                        	$(".sw-channel-dropdown .cs-mega-menu-sections > ul", this).append('
');
                            $(".sw-channel-dropdown > li:not(.cs-mega-menu-info, .cs-mega-menu-sections, .cs-mega-menu-section)", this).slice(0, megaMenuData[i].channelColumnThreeSections).appendTo($(".cs-mega-menu-section-three ul", this));
                            if(jQuery.trim(megaMenuData[i].channelColumnThreeTitle) != "") {
                            	$(".cs-mega-menu-section-three ul", this).prepend('
'+jQuery.trim(megaMenuData[i].channelColumnThreeTitle)+'');
                            }
                        	sectionCount++;
                   		}
                        $(".sw-channel-dropdown",this).wrapInner("
");
                        $(".sw-channel-dropdown > li:not(.cs-mega-menu-info, .cs-mega-menu-sections, .cs-mega-menu-section)", this).remove();
                        $(".sw-channel-dropdown", this).addClass("cs-mega-menu-"+sectionCount+"-sections");

                        // ADD MEGA CHANNEL CLASS
                        $(this).addClass('mega-channel');
                        $(this).addClass("channel-" + index);

                    	$(".sw-channel-dropdown .cs-mega-menu-cont", this).append('
');
                    }

                    if((imgSrc != "") && (!srcCheck) || jQuery.trim(megaMenuData[i].channelCaption) != "") {
                        // ADD IMAGE
                        if((imgSrc != "") && (!srcCheck) && (megaMenuData[i].channelImgSwitch != "false")) {
                            $(".cs-mega-menu-info", this).prepend('');
                        } else {
                        	$(".cs-mega-menu-info", this).addClass("no-image");
                        }

                        // ADD CAPTION
                        if(jQuery.trim(megaMenuData[i].channelCaption) != "") {
                            var channelCaption = '
' + jQuery.trim(megaMenuData[i].channelCaption) + '';
                            $(".cs-mega-menu-info", this).prepend(channelCaption);
                        } else {
                        	$(".cs-mega-menu-info", this).addClass("no-caption");
                        }

                        if((imgSrc != "") && (!srcCheck) && (jQuery.trim(megaMenuData[i].channelCaption) != "")) {
                        	$(".cs-mega-menu-info", this).addClass("cs-mega-menu-full");
                        }

                        if($(".cs-mega-menu-info",this).hasClass("no-image") && $(".cs-mega-menu-info",this).hasClass("no-caption")) {
                        	$(".cs-mega-menu-info", this).remove();
                            $(".cs-mega-menu-sections",this).addClass("no-info");
                        }
  					}
                }      
            });

            // ACCESSIBILITY         
            $(document).on("keydown", ".cs-mega-menu-section a", function(e) { 
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        e.preventDefault();
                        if($(".cs-mega-menu-section a").index(this) == 0) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                        	var index = $(".cs-mega-menu-section a").index(this)-1;
                        	$(".cs-mega-menu-section a").eq(index).focus();
                        }
                    break;             
                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        e.preventDefault();
                        if($(".cs-mega-menu-section a").index(this) == $(".cs-mega-menu-section a").length) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                            var index = $(".cs-mega-menu-section a").index(this)+1;
                            $(".cs-mega-menu-section a").eq(index).focus();
                        }
                    break;
                    case _this.KeyCodes.tab:
                   		e.preventDefault();
                        var index = $(".cs-mega-menu-section a").index(this)+1;
                        $(".cs-mega-menu-section a").eq(index).focus();
                    break;
                  }
            });
        }

	function dynamicContent(){
		// ********** MYSTART ********** //


		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		/*$(".sw-mystart-dropdown.schoollist .sw-dropdown").attr("id","cs-schoollist-dropdown-list");
		$(".sw-mystart-dropdown.schoollist").off();
		$(".sw-mystart-dropdown.schoollist").click(function(){
			$(".sw-dropdown-arrow",this).addClass("rotate-arrow");
			$("#cs-schoollist-dropdown-list").slideDown(300, "swing");
		});

		$(".sw-mystart-dropdown.schoollist").hover(function(){

		}, function(e) {
			e.stopPropagation();
			$(".sw-dropdown-arrow").removeClass("rotate-arrow");
			$("#cs-schoollist-dropdown-list").slideUp(300, "swing");
		});

		$("#sw-mystart-account .sw-dropdown").attr("id","cs-sw-myaccount-list").removeClass("sw-dropdown");
		$("#sw-mystart-account").off();
