if(navigator.userAgent.indexOf("MSIE 8") > -1){	isMSIE8 = 1; }
if(navigator.userAgent.indexOf("MSIE 9") > -1){	isMSIE9 = 1; }
if(navigator.userAgent.indexOf("MSIE 10") > -1){ isMSIE10 = 1; }
