body.script:
$(window).scroll(function() {
    if ( $(window).scrollTop() >= $('.header--trigger').offset().top ) {
        console.log('hit');
        $('.secondary--header--main').addClass('active');
    } else {
        $('.secondary--header--main').removeClass('active');
    }
});

$(document).ready(function() {
    $(".secondary--header--main .hs-menu-depth-1").each(function(i) {
        var text = $(this).find("a").first().text();
        text = text.replace(/\s+/g, '-').replace(/:/g, '').toLowerCase();
        $(this).addClass("smain-"+text);
    });
});

span.script:
$('#school-select').on('change', function () {
    var url = $(this).val(); // get selected value
    if (url) { // require a URL
      window.location = url; // redirect
    }
    return false;
});

body.script:
(function () {
    window.addEventListener('load', function () {
        setTimeout(function () {
            var xhr = new XMLHttpRequest();
            xhr.open('POST', '/_hcms/perf', true /*async*/);
            xhr.setRequestHeader("Content-type", "application/json");
            xhr.onreadystatechange = function () {
                // do nothing.
            };
            var connection = navigator.connection || navigator.mozConnection || navigator.webkitConnection;
            function populateNetworkInfo(name, connection, info) {
                if (name in connection) {
                    info[name] = connection[name];
                }
            }
            var networkInfo = {};
            if (connection) {
                ['type', 'effectiveType', 'downlink', 'rtt'].forEach(function(name) {
                    populateNetworkInfo(name, connection, networkInfo);
                });
            }
            var perfData = {
                url: location.href,
                portal: 2719742,
                content: 5664332094,
                group: -1,
                connection: networkInfo,
                timing: performance.timing
            };
            xhr.send(JSON.stringify(perfData));
        }, 3000);  // Execute this 3 seconds after onload.
    });
})();

