$(document).ready(function() {
    $(slideshowid368).cycle({
        fx: 'fade', // choose your transition type, ex: fade, scrollUp, shuffle, etc...
        width: 980,
		height: 270,
		before: onBefore368,
		cleartypeNoBg: true,
	timeout:    3000 
	});
		
var pb368 = slideshowid368 + 'pauseButton';

$(pb368).click(function() {
	$(slideshowid368).cycle('toggle');
    });
var ff368 = slideshowid368 + 'nextButton';
$(ff368).click(function() {
    $(slideshowid368).cycle('next');
    });
	
var rr368 = slideshowid368 + 'prevButton';
$(rr368).click(function() {
    $(slideshowid368).cycle('prev');
    });
// callback fired when each slide transition begins
function onBefore368(curr,next,opts) {
    var $slide = $(next);
    var w = $slide.outerWidth();
    var h = $slide.outerHeight();
    
    
};		
});

