var jq = jQuery.noConflict(true);

jq(document).ready(function(){
	jq(".countdown-active").TimeCircles({
		"animation": "smooth",
		"bg_width": 1,
		"fg_width": 0.05,
		"text_size": .1,
		"count_past_zero": false,
		"circle_bg_color": "#810000",
		"time": {
			"Days": {
				"text": "Days",
				"color": "#696969",
				"show": true
			},
			"Hours": {
				"text": "Hours",
				"color": "#696969",
				"show": true
			},
			"Minutes": {
				"text": "Minutes",
				"color": "#696969",
				"show": true
			},
			"Seconds": {
				"text": "Seconds",
				"color": "#696969",
				"show": true
			}
		}
	});

	//rebuild when window size changes
	jq(window).resize(function() {
		jq(".countdown-active").TimeCircles().rebuild();
	});

	jq('.spotlight-items-list').slick({
		slidesToShow: 1,
		slidesToScroll: 1
	});
});

