$(function() {
	$('#topnav').accessibleMegaMenu();

	$('#facts_slider').slick({
		slidesToShow: 1,
		adaptiveHeight: true
	});

	$('#calendar_slider').slick({
		slidesToShow: 1,
		adaptiveHeight: true,
		mobileFirst: true,
		responsive: [
			{
				breakpoint: 767,
				settings: {
					slidesToShow: 2
				}
			},
			{
				breakpoint: 1023,
				settings: {
					slidesToShow: 3
				}
			}
		]
	});

	$('#logos_shuffle').slick({
		variableWidth: true,
		autoplay: true,
		arrows: false,
		centerMode: true,
		slidesToShow: 7,
		slidesToScroll: 1,
		autoplaySpeed: 0,
		speed: 3000
	});
});


window.onload = init;
var topnavButton = document.getElementById('topnav_mobile_toggle');
var topnavDisplay = document.getElementById('topnav');

function init() {
	topnavButton.onclick = toggleNav;
}
function toggleNav(){
	topnavDisplay.classList.toggle("open");
}
