            $('.account .sw-dropdown-arrow').css('right', '0');
        }

        // Prevents cahnnel from display blank dropdown
        $('.sw-channel-item').each(function(){
        	var hideSingle = 'yes';
        	var size = $('ul li', this).size();
        	if(size == 0){
            	$('ul', this).remove();
        	}
            if(hideSingle == "yes;no" || hideSingle == "yes"){
            	if(size == 1){
            		$('ul', this).remove();
                }
            }

        });//end each
        if($('.cs-contact-fax').text().length < 6){
        	$('.cs-contact-fax').remove();
        }
	});	

	function jsMediaQueries(){
		// LEFT THIS HERE FOR FUTURE USE IN CASE IT IS NEEDED
		switch(csGetBreakPoint()) {
			case "desktop": 

			break;
			case "768":

			break;
			case "640":

			break;
			case "480":

			break;
			case "320":

			break;
		}
	}

	function responsiveMenu() {
    	var csUseSchoollist = ($(".sw-mystart-dropdown.schoollist").length) ? "yes" : "no";

		// PUT TOGETHER THE GLOBAL ICONS
		var globalIcons = [
			["CONTACT", "https://www.scasd.org/contact", "Communication/14.png"],
			["CALENDAR", "https://www.scasd.org/districtcalendar", "Site Utility/2.png"],
			["PARENT PORTAL/HAC", "http://online.scasd.org", "Information Technology/3.png"],
			["DISTRICT DIRECTORY", "https://www.scasd.org/directory", "People/6.png"],
			["STUDENT REGISTRATION", "https://www.scasd.org/registration", "Classroom/2.png"]
		];
		var iconNum = ("5" == "5;4;3;2;1;0") ? 0 : "5";
		var globalLinksHeader = "", globalLinks = "";

		for(var i = 0; i < iconNum; i++) {
			var linkCheck = (globalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : globalIcons[i][1];
			var gbIcon = "";
			if($.trim(globalIcons[i][2]).indexOf("cms") > -1) {
				gbIcon = "";
			} else {
				var splitSelection = globalIcons[i][2].split("/");
				var iconCat = $.trim(splitSelection[0]);
				iconCat = iconCat.toLowerCase().replace(/\s+/g, "-");
				var iconChoice = splitSelection[1].split(".");
				iconChoice = iconChoice[0];
				var iconSelection = iconCat + "-" + iconChoice;
				gbIcon = "";
			}

			globalLinks += "1, " + gbIcon + " " + globalIcons[i][0] + ", " + linkCheck + ", _self;";
		}

		if(iconNum > 0) {
			globalLinksHeader = ($.trim("Popular Links") == "") ? "Popular Links" : "Popular Links";
		}

		$("body").csRsMenu({
			"breakPoint"		: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"	: "left-to-right", // OPTIONS - left-to-right, right-to-left
			"menuButtonParent"	: "#gb-mystart",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "MENU",
			"showSchools"		: csUseSchoollist,
			"showTranslate"		: csUseTranslate,
			"translateId"		: "none",
			"showAccount"		: "yes",
			"extraMenuOptions"	: globalLinksHeader, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"	: globalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"		: "1"
		});

        if($(".rs-menu-option.schools .rs-menu-option-sublist li.first a").text() == "District Home") {
        	$(".rs-menu-option.schools .rs-menu-option-sublist li.first").hide();
        }
	}

	function pageListMenu() {
		$("body").csRsPagelistMenu({
		        "breakPoint"            : 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
		        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
		        "primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
		        "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
		        "accentColor"           : "#BABABA", // DEFAULT - #BABABA
		        "menuButtonParent"      : ".sp.column.two",
		        "showMenuBtnText"       : "yes",
		        "menuBtnText"           : "PAGES",
		        "siteID"                : "1",
		        "allLoaded"             : function(){ }
		});
	}

