        $("#hp-content-row-three .ui-widget.app.upcomingevents .ui-widget-detail ul.ui-articles li").each(function() {
            var eventNum = $(".ui-article .upcoming-column.right > .ui-article-description", this).length;
            var dataHtml = "";
            if(eventNum > 4){
            	for(i = 5; i <= eventNum; i++){
                	dataHtml += '
';
                	dataHtml += $('.ui-article .upcoming-column.right > .ui-article-description:nth-child(' + [i] + ')').html();
                    dataHtml += '';
                }
                console.log(dataHtml);
                var newColumn = '


' + dataHtml + '';
                $(newColumn).insertAfter($(this));
                $(".ui-article .upcoming-column.right .ui-article-description:nth-child(n + 5)", this).remove()
            }
        });

