	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop": 
				gbContentOffset();
                $(window).scroll(headerScroll);;
			break;
			case "768":
				//appView();
			break;
			case "640":
				//appView();
			break;
			case "480":
				//appView();
			break;
			case "320":
				//appView();
			break;
		}
	}

	function mediaRotator() {
		head.ready("mediaRotator", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
			        // MULTIMEDIA ROTATOR
			        $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                            "imageWidth"              : 1500,
                            "imageHeight"             : 915,
                            "efficientLoad"           : "yes",
                            "appHeader"               : "no",
                            "titles"                  : "no",
                            "captions"                : "no",
                            "galleryOverlay"          : "no",
                            "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
                            "playPause"               : "no",
                            "mainControls"            : "yes",
                            "contMoreLink"            : "no",
                            "contMoreLinkText"        : "Read More",
                            "descMoreLink"            : "no",
                            "descMoreLinkText"        : "Read More",
                            "descVideoLink"           : "no",
                            "descVideoLinkText"       : "Watch Video",
                            "bullets"                 : "no",
                            "bulletControls"          : "no",
                            "thumbnails"              : "no",
                            "thumbnailWidth"          : 122,
                            "thumbnailHeight"         : 53,
                            "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
                            "hoverPause"              : "yes",
                            "autoRotate"              : "yes",
                            "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
                            "fullScreenBreakpoints"   : ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                            "fullScreenVideo"         : "no",
                            "transitionType"          : "fade", // fade or slide
                            "transitionSpeed"         : 2,
                            "transitionDelay"         : 6,
                            "onImageLoad"             : function() { },
                            "allImagesLoaded"         : function() { },
                            "onThumbnailLoad"         : function() { },
                            "allThumbnailsLoaded"     : function() { },
                            "onTransitionStart"       : function() { },
                            "onTransitionEnd"         : function() { },
                            "watchVidClick"           : function() { },
                            "closeVidClick"           : function() { },
                            "allLoaded"               : function() { }

			        });
			}
		});

        if($("#sw-content-container10 .ui-widget.app.multimedia-gallery").length) {
            var mmg = eval("multimediaGallery" + $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
            mmg.props.defaultGallery = false;

            $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                "efficientLoad" : true,
                "imageWidth" : 1229,
                "imageHeight" : 655,
                "mobileDescriptionContainer": [], // [960, 768, 640, 480, 320]
                "galleryOverlay" : true,
                "linkedElement" : ["overlay"], // ["image", "title", "overlay"]
                "playPauseControl" : false,
                "backNextControls" : true,
                "bullets" : false,
                "thumbnails" : false,
                "thumbnailViewerNum": [4, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "autoRotate" : true,
                "hoverPause" : true,
                "transitionType" : "fade", // fade, slide, custom
                "transitionSpeed" : 1.5,
                "transitionDelay" : 4,
                "fullScreenRotator" : false,
                "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                "onTransitionStart" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                "allLoaded" : function(props) {}, // props.element, props.mmgRecords
                "onWindowResize": function(props) {} // props.element, props.mmgRecords
            });
        }
	}
    function gbContentOffset() {
    	var windowWidth = $(window).width();
        var contentWidth = $("#gb-mystart-inner").width();
       	var offset = (windowWidth - contentWidth)/2;
        var spColTwo = $(".sp.column.two").width();
        var spOffSet = offset + spColTwo;
        $("#hp-slideshow .rs-gallery-container .rs-gallery-control.back").css("left", offset);
        $("#hp-slideshow .rs-gallery-container .rs-gallery-control.next").css("right", offset);
        $("#hp-content-row-four .ui-widget.app .ui-widget-header").css("padding-left", offset);
        $("#hp-content-row-four .ui-widget.app .ui-widget-header").css("padding-right", offset);
        $("#hp-content-row-four .ui-widget.app .ui-widget-detail").css("padding-left", offset);
        $("#hp-content-row-four .ui-widget.app .ui-widget-detail").css("padding-right", offset);
        $("#hp-content-row-four .ui-widget.app .ui-widget-footer").css("padding-left", offset);
        $("#hp-content-row-four .ui-widget.app .ui-widget-footer").css("padding-right", offset);
        $("#sp-content-outer:after").css("right",spOffSet);
    }

    var headerScroll = function(){
		var getWidth = $(window).width();
        var scrollTop = $(window).scrollTop();
        var stickyHeaderHeight = $("#sticky-container").height();
        if (getWidth > 768) { // 960+
			if($(window).scrollTop() > stickyHeaderHeight) {
				$("#sticky-container").addClass("sticky");
                $("#hp-slideshow-outer").css("margin-top",stickyHeaderHeight);
                $("#sp-content-outer").css("margin-top",stickyHeaderHeight);
                $("#spn-content-outer").css("margin-top",stickyHeaderHeight);
			} else {
            	$("#sticky-container").removeClass("sticky");
                $("#hp-slideshow-outer").css("margin-top", "0px");
                $("#sp-content-outer").css("margin-top", "0px");
                $("#spn-content-outer").css("margin-top", "0px");
			}
		}

	}

