		}
        }

    function searchControls() {
        $(".gb-search-btn").click(function(e){
            e.preventDefault();
            animateSearch();
        });
        $(".gb-search-btn").keydown(function(e){
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
            	animateSearch();
            }
        });
	}

    function animateSearch() {
        if(!$("#gb-menu-right .gb-search-inner").hasClass("open")) {
            $(".gb-search-btn").attr("aria-label", "Close search").removeClass("closed").addClass("open");
            $("#gb-menu-right .gb-search-inner").addClass("open");
            $("#sw-search-input").attr("tabindex","0");


            $("#gb-menu-right .gb-search-inner").animate({
                left: "-210px"
            }, 500, function() {
                $("#sw-search-input").focus();
            });
        } else {
            $("#gb-menu-right .gb-search-inner").animate({
                left: "100%"
            }, 500, function() {
                $(".gb-search-btn").attr("aria-label", "Open search").addClass("closed").removeClass("open").focus();
                $("#gb-menu-right .gb-search-inner").removeClass("open");
                $("#sw-search-input").attr("tabindex","-1");
            });
        }
    }
    function translateControls() {
        $(".gb-translate-btn").click(function(e){
            e.preventDefault();
            animateTranslate();
        });
        $(".gb-translate-btn").keydown(function(e){
            if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
            	animateTranslate();
            }
        });
	}
    function animateTranslate() {
        if(!$("#gb-menu-right .gb-translate-inner").hasClass("open")) {
            $(".gb-translate-btn").attr("aria-label", "Close translate menu").removeClass("closed").addClass("open");
            $("#gb-menu-right .gb-translate-inner").addClass("open");
            $("#gb-menu-right .sw-mystart-dropdown.translate .sw-dropdown-list li a").attr("tabindex","0");


            $("#gb-menu-right .gb-translate-inner").animate({
                left: "-210px"
            }, 500, function() {
                $("#gb-menu-right .sw-mystart-dropdown.translate .sw-dropdown-list li:first-child a").focus();
            });
        } else {
            $("#gb-menu-right .gb-translate-inner").animate({
                left: "100%"
            }, 500, function() {
                $(".gb-translate-btn").attr("aria-label", "Open translate menu").addClass("closed").removeClass("open").focus();
                $("#gb-menu-right .gb-translate-inner").removeClass("open");
                $("#gb-menu-right .sw-mystart-dropdown.translate .sw-dropdown-list li a").attr("tabindex","-1");
            });
        }
    } 

    function globalIconFunctionality(container,button,ariaLabel) {
        if(!$("#global-icons-container").hasClass("open")){
            //$("#global-icons-container").slideDown("slow");
            container.slideDown("slow").removeClass("hidden").attr("aria-hidden","false");
            button.removeClass("closed").addClass("open").attr("aria-label","Click to close " + ariaLabel + " menu").attr("aria-expanded","true");
            //container.removeClass("hidden").attr("aria-hidden","false");
            $("#global-icons-container").removeClass("closed").addClass("open").attr("aria-hidden","false");
            container.find(".global-icon:first-child").focus();

        } else {
            //$("#global-icons-container").slideUp("slow");
            button.removeClass("open").addClass("closed").attr("aria-label","Click to open " + ariaLabel + " menu").attr("aria-expanded","false").focus();
            container.slideUp("slow").addClass("hidden").attr("aria-hidden","true");
            $("#global-icons-container").removeClass("open").addClass("closed").attr("aria-hidden","true");
        }

        $('.global-icons-closing-outer').on('click keypress', function(event){
          if(a11yClick(event) === true){
          		event.preventDefault();
              	//$("#global-icons-container").slideUp("slow");
            	button.removeClass("open").addClass("closed").attr("aria-label","Click to open " + ariaLabel + " menu").attr("aria-expanded","false").focus();
            	container.slideUp("slow").addClass("hidden").attr("aria-hidden","true");
            	$("#global-icons-container").removeClass("open").addClass("closed").attr("aria-hidden","true");
          	}
        }); 


    }

    function globalIconActions() {

        $('.gb-link-button').on('click keypress', function(event){
          if(a11yClick(event) === true){
          	event.preventDefault();
            var newContainer = "#" + $(this).attr('data-rel');

            globalIconFunctionality($(newContainer),$(this),"parents");
          }
        });



    }
