    function stickyCalendarFix() {
    	var headerHeight = $("header").height();
        var channelHeight = $("nav").height();
        var calendarHeaderOffset = headerHeight + channelHeight;

        if ($(window).scrollTop() <= calendarHeaderOffset) {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                $(".wcm-controls").css("margin-top", "0px");
            }
        } else {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", calendarHeaderOffset + "px");
                }
            }
        }
    }

	function searchText() {
		//CUSTOM MYSTART SEARCH PLACEHOLDER
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search keywords");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search keywords") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search keywords");
			}
		});
	}

	function modEvents(){
		head.ready("modEvents", function() {
			if (!$("#hp-content-row-one .upcomingevents .joel-month").length) {
				$("#hp-content-row-one .upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "yes"
				});
				eventsByDay("#hp-content-row-one .upcomingevents .ui-articles");
			}
            if (!$("#hp-content-row-two .upcomingevents .joel-month").length) {
				$("#hp-content-row-two .upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "yes"
				});
				eventsByDay("#hp-content-row-two .upcomingevents .ui-articles");
			}
            if (!$("#hp-content-row-three upcomingevents .joel-month").length) {
				$("#hp-content-row-three .upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "no"
				});
				eventsByDay("#hp-content-row-three .upcomingevents .ui-articles");
			}
            if (!$("#sp-content .upcomingevents .joel-month").length) {
				$("#sp-content .upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "yes"
				});
				eventsByDay("#sp-content .upcomingevents .ui-articles");
			}
		});
	}		

	function eventsByDay(container) {
		//MOVE ALL EVENTS WITH THE SAME DATES INTO THE SAME CONTAINER
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};

            if($('.upcoming-column.left h1', this).length){
                var newDateTime = $('.upcoming-column.left h1', this).html().split("");
                var newDate = '';

                newDate += newDateTime[0] + ' •';
                //$(".upcoming-column.right", this).prepend(newDate);
                //$(".ui-article-description", this).prepend(newDate);
                //$(".upcoming-column.right .ui-article-description", this).each(function(){
                //	$(this).prepend(newDate);
                //});
            }    

            //add an ALL DAY label if no time was given
            $('.upcoming-column.right .ui-article-description', this).each(function(){
                if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                    $(this).prepend('ALL DAY');
                } else {
                    $(this).prepend(newDate);
                }
            }); 

            //REMOVE ALL INSTANCES OF :00 in UPCOMING EVENT TIMES
            $("div.ui-widget.app.upcomingevents div.upcoming-column-container div.upcoming-column.right .ui-article-description").each(function(){
                var time = $(".sw-calendar-block-time", this).text().replace(/\:00/g,"");
                $(".sw-calendar-block-time", this).text(time);
            });

		});

         $(".ui-articles").addClass("ui-clear");

        $('.hp.column.three .ui-widget.app.upcomingevents .ui-widget-detail .ui-article-title').each(function(){
            $(".joel-month",this).appendTo(this);
        });



	}

