$(document).ready(function(){
    var away = false;
    
    $(document).scroll(function(e) {
        if ($(document).scrollTop() > 0) {
            if (!away) {
				if($('#theme').hasClass('subpage')) {
                away = true;
				
					if ($('.RadMenu_PageControls').length > 0 || $('#CMS_admin_ToolBar').length > 0 ) {
					
						$('.header-stick').stop().css({ position : 'fixed' , top : '35px', borderBottom: '6px solid #134e8c' }).animate({backgroundColor: "#ffffff"}, 500);
						
					}
					
					
					else {
					
						$('.header-stick').stop().css('position' , 'fixed').animate({backgroundColor: "#ffffff", borderBottom: '6px solid #134e8c' }, 500);
						
					}
				



if ($('.RadMenu_PageControls').length > 0 || $('#CMS_admin_ToolBar').length > 0) {

                	$('.header-stick').stop().css({ position : 'fixed' , top : '35px', width : '100%', maxWidth : '100%', left : '0px', borderBottom: '6px solid #134e8c'  }).animate({ backgroundColor: "#ffffff"}, 500);

}

else {

                	$('.header-stick').stop().css({ position : 'fixed' , top : '0px', width : '100%', maxWidth : '100%', left : '0px', borderBottom: '6px solid #134e8c'  }).animate({ backgroundColor: "#ffffff"}, 500);

}

					$('#header-left .logo').stop().animate({ width : '75px' , margin : '73px auto 0px' }, 500);
					$('body .RadMenu .rmHorizontal > li > a > .rmText').stop().animate({ padding : '50px 13px 29px 13px' , color : '#134e8c' }, 500);
					$('#header-left').stop().animate({ width : '15%' }, 500);
					$('#header-right').stop().animate({ width : '85%' }, 500);
					$('.megamenu.mega_menu ul.level0 a').stop().css("color" , "#122F52" );
					$('.megamenu.mega_menu .mega-dd a.m_link').stop().css("color" , "#fff" );
					$('#subhideLogo').stop().css("display" , "none" );
				}
				
				
            }
        } else {
			
			if($('#theme').hasClass('subpage')) {
            away = false;
            $('.header-stick').stop().css({ position : 'absolute' , top : '0px', maxWidth : '1200px', borderBottom: 'none' }).animate({backgroundColor: "transparent"}, 500);
			$('#header-left .logo').stop().animate({ width : '100%' , margin : '20px auto 0px' }, 500);
			$('body .RadMenu .rmHorizontal > li > a > .rmText').stop().animate({ padding : '100px 13px 15px 13px' , color : '#ffffff' }, 500);
			$('#magic-line').stop().animate({ borderColor : '#ffffff' }, 500);
			$('#header-right').stop().animate({ width : '80%' }, 500);
			$('#header-left').stop().animate({ width : '19%' }, 500);
			$('.megamenu.mega_menu ul.level0 a').stop().css("color" , "#fff" );
			$('.megamenu.mega_menu .mega-dd a.m_link').stop().css("color" , "#fff" );
			$('#subhideLogo').stop().css("display" , "block" );



	var stickWidth = $('.header-stick').width();
	
	var windowWidth = $(window).width();
	
	$('.header-stick').css({
		
		left : ( (windowWidth - stickWidth ) / 2 ) + 'px'
		
	});




			
			}
		}
    });
	
	if(!$('#dtest').length > 0) {
		if($('#theme').hasClass('homepage')) {
			$('#header').addClass('frozen');
                        $('#page').addClass('frozen');
		}
	}


if ($("#ctl00_ContentPlaceHolder1_ctl00_ctl00_menu").length || $("#CMS_admin_ToolBar").length){
  $('#ctl00_lnkGateway').text('SM Website Logout');
} else {
  $('#ctl00_lnkGateway').text('SM Website Login');
};

// For featured boxes header image
if( $('.subpage.featured').length && $('#featured_image').length ){
  var featuredImg = $('#featured_image').attr('src');
  $('.subpage #subHeader-bg, .subpage .mobile-header').css('background-image', 'url("'+ featuredImg +'")');
}
				
});

