	$(window).load(function(){
		if($(window).width() >= 320 && $(window).width() <= 639){
			calendarListView();
		}
		searchText();

		$("div.ui-widget.app.navigation li div.bullet").click(function(){
			return true;
		});

        //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-mystart-dropdown.schoollist").unbind("hover");
		$(".sw-mystart-dropdown.schoollist").hover(function(){

			},function(){
			$(".sw-dropdown-arrow",this).removeClass("open");
			$(this).removeClass("open");
			$(".sw-dropdown", this).slideUp("slow", "swing");
		});

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-mystart-dropdown.translate").unbind('hover');
		$(".sw-mystart-dropdown.translate").hover(function(e){

		}, function(e) {
			//e.stopPropagation();
			$(".sw-dropdown-arrow").removeClass("rotate-arrow");
			$(this).removeClass("open");
			$(".sw-dropdown").slideUp("slow", "swing");
		});

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$("#sw-mystart-account").unbind('hover');
		$("#sw-mystart-account").hover(function(){

		}, function() {
			$(".icon-font-arrow",this).removeClass("open");
			$("#cs-sw-myaccount-list").slideUp("slow", "swing");
		});

		//UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
            megMenuResize();
		}, function(){
			$(".sw-channel-item ul").stop(true, true);
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

	});

	head.load([
		{ rsMainMenu: "//extend.schoolwires.com/creative/scripts/creative/responsive/creative-responsive-menu-v2/creative.responsive.menu.v2.min.js" },
		{ rsPagelistMenu: "//extend.schoolwires.com/creative/scripts/creative/responsive/creative-responsive-pagelist-menu-v2/creative.responsive.pagelist.menu.v2.min.js" },
		{ mediaRotator: "//extend.schoolwires.com/creative/scripts/creative/rotate/cs.rs.multimedia.rotator.v2.min.js" },
		{ globalIcons: "//extend.schoolwires.com/creative/scripts/creative/tools/creative-icons-v2/creativeIcons.v2.min.js" },
		{ modEvents: "//extend.schoolwires.com/creative/scripts/joel/mod-events/joel.mod-events.min.js" }
	]);

	$(function(){

		$(window).bind("resize", function(){ jsMediaQueries();megMenuResize(); });

		head.ready("rsMainMenu", function(){
			var globalHeader = "", additionalLinks = "", headerLinkNum = "", linksHeader = "";

			// MENU GLOBAL ICONS
			var menuGlobalIcons = [
				["Calendar", "[SITECALENDARLINK]"],
				["Job Vacancies", "/Page/604"],
				["Menus", "https://rockhill.nutrislice.com/menu"],
				["Tip Line", "/Page/6778"],
				["Transportation", "/Domain/303"],
                ["Canvas", "https://rockhill.instructure.com/login/canvas"]
			];
			var menuIconNum = ("6" == "6;5;4;3;2;1;0") ? 0 : "6";

			for(var i = 0; i < menuIconNum; i++) {
				var linkCheck = (menuGlobalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : menuGlobalIcons[i][1];
				additionalLinks += "1, " + menuGlobalIcons[i][0] + ", " + linkCheck + ", _self;";
			}

			if(menuIconNum > 0) {
				globalHeader = "Popular Links;";
			}

            // MENU HEADER LINKS
			var headerLinks = [
				["Staff Email", "https://login.microsoftonline.com", "_blank"],
                ["Apply Now", "/Page/604", "_self"],
                ["Staff Resources", "/Page/4326", "_self"]
			];
			for(var l = 0; l < 3; l++) {
                if(headerLinks[l][0] !=""){
                        additionalLinks += "2, " + headerLinks[l][0] + ", " + headerLinks[l][1] + ", " + headerLinks[l][2] + ";";
                        var headerLinkNum = 1;
                    }
            }    

			if(headerLinkNum = 1) {
				linksHeader = "Header Links";
			}


			// RESPONSIVE MAIN MENU
			$("body").csRsMenu({
			        "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
			        "primaryColor"          : "#1b3860", // DEFAULT - #2C363E
			        "secondaryColor"        : "#108276", // DEFAULT - #222A30
			        "accentColor"           : "#a3d8ca", // DEFAULT - #B5BBBF
			        "menuButtonParent"      : "#gb-menu-inner",
			        "showMenuBtnText"       : "yes",
			        "menuBtnText"           : "MENU",
			        "showSchools"           : "yes",
			        "showTranslate"         : "yes",
			        "translateId"           : "none",
			        "showAccount"           : "yes",
			        "extraMenuOptions"      : globalHeader + linksHeader, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			        "extraMenuItems"        : additionalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			        "siteID"                : "4"
			});

		});

		head.ready("rsPagelistMenu", function(){
			// RESPONSIVE PAGELIST MENU
			$("body").csRsPagelistMenu({
			        "breakPoint"            : 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
			        "primaryColor"          : "#1b3860", // DEFAULT - #2C363E
			        "secondaryColor"        : "#108276", // DEFAULT - #222A30
			        "accentColor"           : "#a3d8ca", // DEFAULT - #B5BBBF
			        "menuButtonParent"      : "#sp-content",
			        "showMenuBtnText"       : "yes",
			        "menuBtnText"           : "PAGES",
			        "siteID"                : "4"
			});
		});	

		head.ready("globalIcons", function() {
			var csIconNum = ("6" == "6;5;4;3;2;1;0") ? "0" : "6";
			$("#gb-global-icons").creativeIcons({
			        "iconNum"      : csIconNum, // NUMBER OF ICONS
			        "iconColor"    : "#FFFFFF", // ICON COLOR
			        "icon1"        : ["Site Utility/3.png", "Calendar", "[SITECALENDARLINK]", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			        "icon2"        : ["/cms/lib011/SC01000464/Centricity/Template/GlobalAssets/icons/jobs-global-icon.png", "Job Vacancies", "/Page/604", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
