	function searchText() {
		//CUSTOM MYSTART SEARCH PLACEHOLDER
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}


	function modEvents(){
		head.ready("modEvents", function() {
			if (!$(".upcomingevents .joel-month").length) {
				$(".upcomingevents").modEvents({
					columns     : "yes",
					monthLong   : "no",
					dayWeek     : "no"
				});
				eventsByDay(".upcomingevents .ui-articles");
			}
		});
	}		

	function eventsByDay(container) {
		//MOVE ALL EVENTS WITH THE SAME DATES INTO THE SAME CONTAINER
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();

			};
		});

        $('.ui-widget.app.upcomingevents .ui-widget-detail .ui-articles .upcoming-column.right .ui-article-description').each(function(){
			$(".sw-calendar-block-time", this).before($(".sw-calendar-block-title",this));
		});

		//ADD CLASSES TO EACH UPCOMING EVENT LI IF LIMITING THEM - REMOVE IF NOT NECESSARY
		if($(".hp").length) {

			// ADD CLASSES TO ARTICLES
			$("#hp-region-three .upcomingevents .ui-articles").wrap("
");
			$("#hp-region-three .upcomingevents li").first().addClass("first");
			$("#hp-region-three .upcomingevents li").last().addClass("last");
			var eventClass = ["one", "two", "three", "four", "five"];
			$("#hp-region-three .upcomingevents li").each(function() {
				var thisIndex = $(this).index();
				$(this).addClass("event-" + eventClass[thisIndex]);
			});
			$('#hp-region-three .ui-widget.app.upcomingevents .ui-widget-detail .ui-articles').each(function(){
				$(this).addClass("ui-clear");
			});
		}
	}

	function calendarListView(){
		//AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}
    function megaMenuDropdown() {
        var megaMenuData = [
            [
                ["/cms/lib/SC01000464/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", "", ""]
            ],
            [
                ["/cms/lib/SC01000464/Centricity/Template/GlobalAssets/images///background-images/mega-menu1.jpg", "The class of 2016 earned more than $30 million in scholarships and grants.", "High Achieving Students"]
            ],
            [
                ["/cms/lib/SC01000464/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", "", ""]
            ],
            [
                ["/cms/lib/SC01000464/Centricity/Template/GlobalAssets/images///background-images/mega-menu2.jpg", "", "Thank you for being a part of our family!"]
            ],
            [
                ["/cms/lib/SC01000464/Centricity/Template/GlobalAssets/images///background-images/biking.jpg", "Rock Hill is a 4-time winner of America&#39s Promise Alliance 100 Best Communities for Young People. ", "All-American City"]
            ],
            [
                ["/cms/lib/SC01000464/Centricity/Template/GlobalAssets/images///background-images/mega-menu3.jpg", "", "We have the best teachers in South Carolina."]
            ],
            [
                ["/cms/lib/SC01000464/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", "", ""]
            ],
            [
                ["/cms/lib/SC01000464/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", "", ""]
            ],
            [
                ["/cms/lib/SC01000464/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", "", ""]
            ],
            [
                ["/cms/lib/SC01000464/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", "", ""]
            ]
        ];

        // BUILD THE MEGA MENUS
        $("#sw-channel-list-container li.sw-channel-item").each(function(){
            // LOWERCASE THE CHANNEL NAME
            var channelText = $("> a span", this).text().toLowerCase();

            // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
            if(channelText == "home") {
                $(this).addClass("home");
            } else if(channelText == "calendar") {
                $(this).addClass("calendar");
            }

            //IGNORE HOME AND CALENDAR CHANNELS
            if(channelText != "home" && channelText != "calendar") {
                // SET THE CHANNELS INDEX
                var i = ($("#sw-channel-list-container li.sw-channel-item.home").length) ? $(this).index() - 1 : $(this).index();

                // ADD MEGA CHANNEL CLASS
                $(this).addClass("mega-channel");

                // ADD STRUCTURE TO THE DOM
                $(".sw-channel-dropdown", this).removeAttr("class").wrap('

');

                var caption = jQuery.trim(megaMenuData[i][0][1]);
                var header = jQuery.trim(megaMenuData[i][0][2]);
                if(caption != "" || header != "") {
                    var captionColumn = '
';
                }  
                if(header != ""){
                    // ADD HEADER
                    captionColumn += '
' + header + '';
                }        
                if(caption != ""){
                     // ADD CAPTION
                    captionColumn += '
' + caption + '';
                }    
                if(caption != "" || header != "") {
                    captionColumn += ''
                }    
                $(".sw-channel-dropdown .mega-columns", this).prepend(captionColumn);

                // ADD IMAGE
                var imgColumn = '';
                var imgSrc = jQuery.trim(megaMenuData[i][0][0]);
                var srcSplit = imgSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if(imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
                    imgColumn = '
';
                }
                $(".sw-channel-dropdown .mega-columns", this).prepend(imgColumn);

                $("li.mega-channel ul.sw-channel-dropdown .mega-column.three ul").addClass("ui-clear");
                $("li.mega-channel ul.sw-channel-dropdown .mega-column.three ul li a").each(function() {

                	$(this).attr("tabindex","0");
                });

            }

            if($("ul.sw-channel-dropdown .mega-column.one", this).length && $("ul.sw-channel-dropdown .mega-column.two", this).length  && $("ul.sw-channel-dropdown .mega-column.three", this).length) {

            } else if($("ul.sw-channel-dropdown .mega-column.one", this).length && !$("ul.sw-channel-dropdown .mega-column.two", this).length  && !$("ul.sw-channel-dropdown .mega-column.three", this).length) {
            	$("ul.sw-channel-dropdown .mega-column.one", this).css("width","100%");
                $("ul.sw-channel-dropdown .mega-column.one", this).css("margin-right","0px");
            } else if(!$("ul.sw-channel-dropdown .mega-column.one", this).length && $("ul.sw-channel-dropdown .mega-column.two", this).length  && !$("ul.sw-channel-dropdown .mega-column.three", this).length) {
            	$("ul.sw-channel-dropdown .mega-column.two", this).css("width","100%");
                $("ul.sw-channel-dropdown .mega-column.two", this).css("margin-right","0px");
            } else if(!$("ul.sw-channel-dropdown .mega-column.one", this).length && !$("ul.sw-channel-dropdown .mega-column.two", this).length  && $("ul.sw-channel-dropdown .mega-column.three", this).length) {
            	$("ul.sw-channel-dropdown .mega-column.three", this).css("width","100%");
                $("ul.sw-channel-dropdown .mega-column.three", this).css("margin-right","0px");
                $("ul.sw-channel-dropdown .mega-column.three li", this).css("width","16.666%");
            } else if($("ul.sw-channel-dropdown .mega-column.one", this).length && $("ul.sw-channel-dropdown .mega-column.two", this).length  && !$("ul.sw-channel-dropdown .mega-column.three", this).length) {
            	$("ul.sw-channel-dropdown .mega-column.one", this).css("margin-right","12%");
                $("ul.sw-channel-dropdown .mega-column.two", this).css("width","66.4%");
                $("ul.sw-channel-dropdown .mega-column.two", this).css("margin-right","0px");
            } else if(!$("ul.sw-channel-dropdown .mega-column.one", this).length && $("ul.sw-channel-dropdown .mega-column.two", this).length  && $("ul.sw-channel-dropdown .mega-column.three", this).length) {
            	$("ul.sw-channel-dropdown .mega-column.two", this).css("width","48%");
                $("ul.sw-channel-dropdown .mega-column.three", this).css("width","50%");
            } else if($("ul.sw-channel-dropdown .mega-column.one", this).length && !$("ul.sw-channel-dropdown .mega-column.two", this).length  && $("ul.sw-channel-dropdown .mega-column.three", this).length) {
            	$("ul.sw-channel-dropdown .mega-column.one", this).css("margin-right","12%");
                $("ul.sw-channel-dropdown .mega-column.three", this).css("width","66.4%");
                $("ul.sw-channel-dropdown .mega-column.three", this).css("margin-right","0px");
                $("ul.sw-channel-dropdown .mega-column.three li", this).css("width","25%");
            }
        });

    }

    function megMenuResize() {
    	$("#sw-channel-list-container li.sw-channel-item").each(function(){
              var windowWidth = $(window).width();
              var contentWidth = $("#gb-header-inner").width();
              var channelItemOffset = $(this).offset();
              var channelItemOffsetLeft = (channelItemOffset.left);
              var channelBarOffset = ((windowWidth-contentWidth)/2);
              var channelDropdownOffset = (channelItemOffsetLeft - channelBarOffset);

              $('ul.sw-channel-dropdown', this).css("left", -channelItemOffsetLeft );
              $('ul.sw-channel-dropdown', this).css("width", windowWidth );
              $('ul.sw-channel-dropdown > li', this).css("padding-left", channelBarOffset);

         });  


    }

	function buildStyleSheet(){
		var headerPrimaryColor60 = "rgba(" + hexToRgb('#234a72') + ", .6)";
        var headerPrimaryColor94 = "rgba(" + hexToRgb('#234a72') + ", .94)";
        var globalIconColor75 = "rgba(" + hexToRgb('#FFFFFF') + ", .75)";
        var contentPrimaryColor65 = "rgba(" + hexToRgb('#234a72') + ", .65)";
        var contentSecondaryColor50 = "rgba(" + hexToRgb('#019388') + ", .5)";
		var secondaryColor = "#019388";
		secondaryColor = secondaryColor.replace('#', '');

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles =	'.hp #gb-channel-list-outer {' +
					'	background: ' + headerPrimaryColor60 + ';' +
					'}' +
                    'li.mega-channel ul.sw-channel-dropdown {' +
					'	background: ' + headerPrimaryColor94 + ';' +
                    '}' +
					'#gb-global-icons-outer .global-icon .icon {' +
					'	color: ' + globalIconColor75 + ';' +
                    '}' +
                    '#gb-global-icons-outer .global-icon .text {' +
					'	color: ' + globalIconColor75 + ';' +
                    '}' +
					'div.ui-widget.app.upcomingevents div.upcoming-column-container div.upcoming-column.left {' +
					'	border: 2px solid ' + contentSecondaryColor50 + ';' +
                    '}' +
                    '.hp.column.one .ui-widget.app.headlines .ui-widget-detail .ui-article-thumb:hover .img:before {' +
					'	background: ' + contentPrimaryColor65 + ';' +
                    '}' +
					'#hp-scroll:before {' +
					'	border-color: transparent transparent ' + headerPrimaryColor60 + ' transparent;' +
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

    function accessibility() {
    	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

        $(".sw-mystart-dropdown").each(function(i) {
            // SET ATTRIBUTES
            $(".selector", this).attr("id", "mystart-dropdown-" + (i + 1));
            $(".sw-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".selector", this).attr("id"));
            $(".sw-dropdown", this).attr("aria-hidden", "true");
            $(".sw-dropdown-list li", this).attr("role", "menuitem");
            $(".sw-dropdown-list li a", this).attr("tabindex", "0");
        });

        $("#sw-search-button").attr("role","button").attr("aria-label","Search button");
        $("#sw-search-input").attr("aria-label","Search input");

        $(".sw-mystart-dropdown").focus(function(e) {
            // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
            if(!$(this).hasClass("open")) {
                // TOGGLE THE DROPDOWN
                $(".sw-dropdown-arrow", this).toggleClass("open");
                $(this).toggleClass("open");
                $(".sw-dropdown", this).slideDown("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");

                $(".sw-dropdown-list li a", this).removeAttr("tabindex");
            }
        });

        $(".sw-mystart-dropdown").focusout(function() {
            var $this = $(this);

            // GIVE THE DOCUMENT TIME TO FOCUS THE NEXT ITEM
            setTimeout(function() {
                // IF THIS DROPDOWN OR ANY OF ITS CHILDREN DO NOT HAVE FOCUS
                if(!$this.is(":focus") && !$this.find(":focus").length) {
                    // TOGGLE THE DROPDOWN
                    $(".sw-dropdown-arrow", $this).toggleClass("open");
                    $this.toggleClass("open");
                    $(".sw-dropdown", $this).slideUp("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", $this).attr("aria-hidden") == "true") ? "false" : "true");
                }
            }, 10);
        });

         $(".sw-mystart-dropdown").keydown(function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME DOWN ARROW
                case csKeyCodes.down:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(".sw-dropdown-list li:first-child a", this).focus();
                break;
            }
        });
        $("#sw-myaccount").focus(function(){
			$("#cs-sw-myaccount-list").slideToggle("slow", "swing");
		});
        $("#sw-myaccount").focusout(function() {
            var $this = $(this);

            // GIVE THE DOCUMENT TIME TO FOCUS THE NEXT ITEM
            setTimeout(function() {
                // IF THIS DROPDOWN OR ANY OF ITS CHILDREN DO NOT HAVE FOCUS
                if(!$this.is(":focus") && !$this.find(":focus").length) {
                    // TOGGLE THE DROPDOWN
                    $this.toggleClass("open");
                    $("#cs-sw-myaccount-list").slideUp("slow", "swing");
                }
            }, 10);
        });

    }

    var parallaxScroll = function(){
		var getWidth = $(window).width();
        var channelHeight = $("#gb-channel-list-outer").height() + 5;
        var headerHeight = $("#gb-header-outer").height();
        var scrollOffset = channelHeight + headerHeight + 40;
		if (getWidth > 768) { // 960+
			if($(window).scrollTop() > scrollOffset) {
				$("#gb-channel-list-outer").addClass("fixed");
                $("#gb-channel-list span.close").removeClass("hidden");
                $("#gb-channel-list-overlay").addClass("open");

			} else {
				$("#gb-channel-list-outer").removeClass("fixed");
                $("#gb-channel-list span.close").addClass("hidden");
                $("#gb-channel-list-overlay").removeClass("open");
			}
		}

	}
    var galleryScroll = function(){
		var getWidth = $(window).width();
        var scrollTop = $(window).scrollTop();
        var scrollOffset = 44 - scrollTop;
		if (getWidth > 768) { // 960+
			if($(window).scrollTop() <= 44) {
				$("#hp-scroll").css("bottom",scrollOffset)
			} else {
            	$("#hp-scroll").css("bottom", "0px");
			}
		}

	}

