meta.script:
$(document).ready(function () {
swap_val = [];
$(".webform-client-form .form-text, .webform-client-form .form-textarea, .block-search .form-text").each(function(i){
swap_val[i] = $(this).val();
$(this).focus(function(){
if ($(this).val() == swap_val[i]) {
$(this).val("");
}
}).blur(function(){
if ($.trim($(this).val()) == "") {
jQuery(this).val(swap_val[i]);
}
});
});
});
