        window.resizeMiniCalendar20200420023143190 = function () {
        var calId = 'miniCal20200420023143190';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200420023143190';
        var calMonthFullId = 'miniCalMonthFull20200420023143190';
        var calMonthShortId = 'miniCalMonthShort20200420023143190';
        var calGoToCalFullId = 'miniCalGotToCalFull20200420023143190';
        var calGoToCalShortId = 'miniCalGoToCalShort20200420023143190';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

