        "Header": function() {
            // FOR SCOPE
            var template = this;

            //SHOW USER OPTIONS IF THEY ARE LOGGED IN
            if($("#sw-mystart-account").length){
                $(".ac-dropdown-container.user-options").show();
                $(".ac-mystart-button.guest").remove();
                $(".ac-mystart-button.user").show();
                $("#sw-mystart-mypasskey span").text("Passkeys");
                $("#sw-mystart-mypasskey").addClass("ac-mystart-button").removeClass("sw-mystart-button").prependTo("#gb-mystart-right");
            } else {
                //HIDE REGISTER BUTTON IF HIDDEN IN SITE MANAGER
                if(!$("#ui-btn-register").length){
                    $(".ac-mystart-button.signin").show();
                    $(".ac-mystart-button.register").remove();
                } else {
                    $(".ac-mystart-button.guest").show();
                    $(".ac-dropdown-container.user-options, .ac-mystart-button.signout").remove();
                }
            }



            //QUICK LINKS
            $("#quick-links").on('click keypress', function(event){
                var quickLinkButton = $(this);
                if($("#gb-page.sticky-header").length){
                	var screenCoverTop = 73;
                } else {
                	var screenCoverTop = $("header").height();
                }
                if(template.AllyClickForButtons(event) === true){
                	event.preventDefault();
                    if($(quickLinkButton).hasClass("open")){
                        $("#screen-cover").fadeOut(300, function(){
                            $("#screen-cover").attr("aria-hidden","false");
                        });
                        $(quickLinkButton).removeClass("open").attr("aria-expanded","false");
                        $("#gb-page").removeClass("quick-links");
                    } else {
                    	$("#screen-cover").css("top",screenCoverTop);
                        $("#screen-cover").fadeIn(300,function(){
                        	$("#screen-cover").attr("aria-hidden","true");
                            $("#quick-links-icons ul").find("li:first a").focus();
                        });
                        $(quickLinkButton).addClass("open").attr("aria-expanded","true");
                        $("#gb-page").addClass("quick-links");
                    }
                }
            })

            $("#close-quick-links").on('click keypress', function(event){
                var quickLinkButton = $("#quick-links");

                $("#screen-cover").fadeOut(300, function(){

                });
                $(quickLinkButton).removeClass("open").attr("aria-expanded","false");
                $("#gb-page").removeClass("quick-links");
                $(quickLinkButton).focus();

            })

        },

