        "modEvents": function(){
            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "no",
                    dayWeek     : "yes"
                });
               eventsByDay(".upcomingevents .ui-articles");
            }

            function eventsByDay(container) {
                var countLi = 0;
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                }); 

                $(".ui-article", container).each(function(){
                    var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
                    var newDate = '';
                    var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                    //if we have the day of the week make sure to include it.
                    if( dateIndex > 1 ){ 
                        newDate += newDateTime[0] + "";
                    }

                    //add in the month
                    newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                    //wrap the date in a new tag
                    newDate += '' + newDateTime[dateIndex] + '';

                    //append the date and month back into their columns
                    $('.upcoming-column.left h1', this).html(newDate);  


                    //add an ALL DAY label if no time was given
                    $('.upcoming-column.right .ui-article-description', this).each(function(){
                        if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                            $(this).prepend('ALL DAY');
                        }
                        $(".sw-calendar-block-time", this).appendTo($(this));
                    });
                });  
            }  
        },

        "Body": function() {
        	//HIDE SCROLL TO TOP WHEN IDLE
            var scrollTimer;

            scrollTimer = setTimeout(noScroll, 3000); // Initial start of timer
            $(window).bind('scroll', function(event) { // When scrolled
                  clearTimeout(scrollTimer); // Stop the existing timer
                  scrollTimer = setTimeout(noScroll, 3000); // And restart
            });


            function noScroll() {
                  $('#gb-page').addClass("idle");
            }

            //REMOVE ALL HIDDEN LINKS
            $("a[data-toggle='false']").remove();

            // CHANGE TEXT ON UPCOMING EVENTS VIEW CALENDAR LINK
            $(".sp .upcomingevents").each(function(){
                $(".view-calendar-link span", this).text("View All Events");
            })

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home").show();

            // ADJUST MORE LINKS
            $(".app").each(function(){
                if($(".more-link", this).length){
                    $(this).addClass("has-more-link");
                    var moreLinkURL = $(this).find(".more-link").attr("href");
                    $(this).find(".ui-widget-detail").append("
view all");
                }   
            })

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            //HP HEADLINES
            $("#hp-content-top .headlines .ui-article-title a").on('focus', function () {
                $(this).closest("li").addClass("hover");
            });

            $("#hp-content-top .headlines .ui-article-title a").on('focusout', function () {
                $(this).closest("li").removeClass("hover");
            });


            //WRAP HEADLINES TEXT IN A CONTENT DIV
            $('#spotlight .headlines ul.ui-articles li').each(function() {
            	$("h1.ui-article-title, p.ui-article-description ", this).wrapAll("
");
            });

            //ADD/REMOVE HOVER CLASS ON SPOTLIGHT LIs
            $('#spotlight li').hover(function() {
            	$(this).addClass('hover');
            }, function() {
            	$(this).removeClass('hover');
            });

            $('#spotlight .ui-article-title a').focus(function() {
            	$(this).closest('li').addClass('hover');
            }).blur(function() {
            	$(this).closest('li').removeClass('hover');
            });



        },

        "Homepage": function() {
            // FOR SCOPE
            var template = this;

            //REMOVE VIDEO H1 IF EMPTY
            if($("#video-text-wrapper h1").text().trim() == ""){
            	$("#video-text-wrapper h1").remove();
            }

            //PLACE THE HEADLINES READ MORE BUTTON INTO THE TOP OF THE APP DETAIL
            $("#hp-content-top .headlines").each(function() {
                if($(".more-link", this).length){
                    $(this).addClass("has-more-link");
                    var moreLinkURL = $(this).find("a.more-link").attr("href");
                    $(this).find(".ui-widget-header").append("
View All");
                    $(this).find(".ui-widget-detail").append("
View All");

                    //REMOVE MORE LINK LI
                    $('#hp-content-top .headlines li:last-child').remove();
                }
            })

            //MOVE HP VIEW CALENDAR LINK INTO THE TOP OF THE APP DETAIL
            $("#hp-content-bottom .upcomingevents").each(function(){
                var calendarLink = $(this).find(".view-calendar-link").attr("href");
                $(this).find(".ui-widget-header").append("
View Full
