                $(this).find(".ui-widget-detail").append("
View Full
Calendar");
            })

            //GET NUMBER OF APPS IN REGION E
            var numEapps = $("#column-two-bottom #sw-content-container4 .app").length;
            if(numEapps >= 3){
                $("#column-two-bottom").addClass("three-plus");
            } else {
                $("#column-two-bottom").addClass("app-"+numEapps);
            }

            //SCROLL DOWN
            $("#scroll-down").on('click keypress', function(event){
                    if(template.AllyClickForButtons(event) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                        $('html, body').animate({
                            scrollTop: $(this).offset().top + 150
                    }, 500, function(){
                            $("#hp-content-top .headlines .ui-widget-header .view-all a").focus();
                    });
                }
            });

        },

        "HomepageHeadlines": function(){
            // FOR SCOPE
            var template = this;

            if(!$("#hp-content-top .headlines").hasClass("initialized")){
                headlinesSlider();
            }
            centerHeadlines();
            headlinesAttributes();


            function headlinesSlider(){
                $("#hp-content-top .headlines").addClass("initialized");

                // WRAP HP BOTTOM HEADLINES INFO IN CONTAINER
                $("#hp-content-top .headlines ul.ui-articles li").each(function() {
                    var dataLink = $(this).find(".ui-article-title a").attr("href");
                    $(this).find(".ui-article-title, .ui-article-description").wrapAll("

");                    
                });

                //LINK TO HEADLINE WHEN CLICKING ANYWHERE ON THE IMAGE
                $(".item-detail-wrapper").on('click keypress', function(event){
                    if(template.AllyClick(event) === true){
                        var headlineURL = $(this).attr("data-link");
                        window.location.assign(headlineURL);                        
                    }
                })

                hfSliderParams.numHeadlines = $("#hp-content-top .headlines li").length;

                //ONLY MAKE THE SLIDER IF THERE ARE MORE THAN 3 HEADLINES
                if(hfSliderParams.numHeadlines > 3){
                	//CENTER SLIDER ON SCREEN
            centerHeadlines();
            //SET ATTRIBUTES
            headlinesAttributes();

                    //WRAP HEADLINES UL IN A CONTAINER
                    $("#hp-content-top .headlines ul").wrap("

");

                    //CLONE LAST ITEM, PLACE IT IN THE FRONT OF LIST
                    $('#hp-content-top .headlines li:last-of-type').clone().prependTo(hfSliderParams.slideContainer);
                    //CLONE FIRST ITEM, PLACE IT IN THE BACK OF LIST
                    $('#hp-content-top .headlines li').eq(1).clone().appendTo(hfSliderParams.slideContainer);

                    hfSliderParams.headlineUlWidth = $("#hp-content-top .headlines li").length * hfSliderParams.slideWidth;
                    $(hfSliderParams.slideContainer).width(hfSliderParams.headlineUlWidth);

                    //ADD CONTROLS
                    $("#hp-content-top #headline-slider-container").prepend("");
                    $("#hp-content-top #headline-slider-container").append("");
                } else {
                    $("#hp-content-top .headlines").addClass("no-slider");
                }

                //ADD RSS BUTTON TO CONTROLS CONTAINER
                $("#hp-content-top .headlines .app-level-social-rss").prependTo("#headline-controls");

                //NAVIGATION
                $(".headlines .slider-button").on('click keypress', function(event){
                    if(template.AllyClickForButtons(event) === true){
                        if(!$(hfSliderParams.slideContainer).hasClass("animating")){
                            headlinesSliderButtons(event);
                        } 
                    }
                })
            }

            function headlinesAttributes(){
                //REMOVE ALL HEADLINES ITEMS FROM THE TAB INDEX
                $("#hp-content-top .headlines ul li a").attr("tabindex","-1");
                $("#hp-content-top .headlines ul li").attr("aria-hidden","true").removeClass("visible");

                //ADD ONLY FULLY VIEWABLE HEADLINES TO THE TAB INDEX
                var lastTabbable = hfSliderParams.count + hfSliderParams.numFullSlides;
                for (i = hfSliderParams.count+1; i <= lastTabbable; i++) { 
                    $("#hp-content-top .headlines ul li:eq("+i+") .ui-article-title a").attr("tabindex","0");
                    $("#hp-content-top .headlines ul li:eq("+i+")").attr("aria-hidden","false").addClass("visible");
                }
            }

            function centerHeadlines(){
                var widthOfCutSlides = ((hfSliderParams.screenWidth - (hfSliderParams.numFullSlides * hfSliderParams.slideWidth)) / 2);
                hfSliderParams.initialPos = (hfSliderParams.slideWidth - widthOfCutSlides) * -1;

                $("#hp-content-top .headlines .slider-button").width(widthOfCutSlides);

                hfSliderParams.headlineUlWidth = $("#hp-content-top .headlines li").length * hfSliderParams.slideWidth;
                $(hfSliderParams.slideContainer).width(hfSliderParams.headlineUlWidth);

                $(hfSliderParams.slideContainer).css("left",hfSliderParams.initialPos);

                hfSliderParams.endingPos = hfSliderParams.initialPos - ((hfSliderParams.numHeadlines - hfSliderParams.numFullSlides) * hfSliderParams.slideWidth);

                hfSliderParams.count = 0;
            }

            function headlinesSliderButtons(event){
                var controlButton = $(event.target);

                $("#hp-content-top .headlines ul li").addClass("visible");

                if($(controlButton).hasClass("prev")){
                      //ONLY SLIDE IF THE FIRST SLIDE IS NOT IN THE FIRST POSITION
                      if(hfSliderParams.count != 0){
                          $(hfSliderParams.slideContainer).addClass("animating");
                          $(hfSliderParams.slideContainer).animate({ left: '+='+hfSliderParams.slideWidth }, "slow", function() {
                              hfSliderParams.count--;
                              headlinesAttributes();
                              $(hfSliderParams.slideContainer).removeClass("animating");
                          });
                      } else {
                          $(hfSliderParams.slideContainer).animate({ left: hfSliderParams.endingPos }, "slow", function() {
                            hfSliderParams.count = hfSliderParams.numHeadlines - hfSliderParams.numFullSlides;
                            headlinesAttributes();
                            $(hfSliderParams.slideContainer).removeClass("animating");
                        });
                      }
                  } 

                  //IF THE NEXT BUTTON IS CLICKED, SLIDE TO THE LEFT
                  if($(controlButton).hasClass("next")){
                    if(hfSliderParams.count != hfSliderParams.numHeadlines - hfSliderParams.numFullSlides){
                        $(hfSliderParams.slideContainer).addClass("animating");
                        $(hfSliderParams.slideContainer).animate({ left: '-='+hfSliderParams.slideWidth }, "slow", function() {
                            hfSliderParams.count++;
                            headlinesAttributes();
                            $(hfSliderParams.slideContainer).removeClass("animating");
                        }); 
                    } else {
                        $(hfSliderParams.slideContainer).animate({ left: hfSliderParams.initialPos }, "slow", function() {
                            hfSliderParams.count = 0;
                            headlinesAttributes();
                            $(hfSliderParams.slideContainer).removeClass("animating");
                        }); 
                    }     
                }
            }
        },

        "HomepageEvents": function(){
            // FOR SCOPE
            var template = this;

            if(!$("#hp-content-bottom .upcomingevents").hasClass("initialized")){
                eventsSlider();
            }
                //CENTER SLIDER
                centerEvents();
                //SET ATTRIBUTES
                eventsAttributes();

            function eventsSlider(){
                $("#hp-content-bottom .upcomingevents").addClass("initialized");

                evSliderParams.numEventDays = $(evSliderParams.slideContainer).find("li").length;

                //ONLY MAKE THE SLIDER IF THERE ARE MORE THAN 5 EVENT DAYS
                if(evSliderParams.numEventDays > 7){
                    //WRAP EVENTS UL IN A CONTAINER
                    $("#hp-content-bottom .upcomingevents ul").wrap("

");

                    //ADD CONTROLS
                    $("#hp-content-bottom .upcomingevents .ui-widget-detail").prepend("");
                    $("").insertAfter("#event-slider-container");
                } else {
                    $("#hp-content-bottom .upcomingevents").addClass("no-slider");
                    //WRAP EVENTS UL IN A CONTAINER
                    $("#hp-content-bottom .upcomingevents ul").wrap("

");
                }

                //NAVIGATION
                $(".upcomingevents .slider-button").on('click keypress', function(event){
                    if(template.AllyClickForButtons(event) === true){
                        if(!$(evSliderParams.slideContainer).hasClass("animating")){
                            eventsSliderButtons(event);
                        }
                    }
                })
            }

            function eventsAttributes(){
                //REMOVE ALL EVENTS FROM THE TAB INDEX
                $("#hp-content-bottom .upcomingevents ul li a").attr("tabindex","-1");
                $("#hp-content-bottom .upcomingevents ul li").attr("aria-hidden","true");

                //ADD ONLY FULLY VIEWABLE EVENTS TO THE TAB INDEX
                var lastTabbable = evSliderParams.count + evSliderParams.numEventsShown;
                for (i = evSliderParams.count; i < lastTabbable; i++) { 
                    $("#hp-content-bottom .upcomingevents ul li:eq("+i+") .sw-calendar-block-title a").attr("tabindex","0");
                    $("#hp-content-bottom .upcomingevents ul li:eq("+i+")").attr("aria-hidden","false");
                }

                //ADD A CLASS TO THE FAR LEFT EVENT FOR REMOVING DIVIDER
                $(evSliderParams.slideContainer).find("li").removeClass("far-left");
                $(evSliderParams.slideContainer).find("li").eq(evSliderParams.count).addClass("far-left");
            }

            function centerEvents(){
                //GET WIDTH OF ALL ITEMS
                evSliderParams.sliderWidth = evSliderParams.numEventDays * evSliderParams.slideWidth;
                $(evSliderParams.slideContainer).find("li").css("width",evSliderParams.slideWidth);
                $(evSliderParams.slideContainer).width(evSliderParams.sliderWidth);

                $(evSliderParams.slideContainer).css("left","0px");

                evSliderParams.endingPos = ((evSliderParams.numEventDays - evSliderParams.numEventsShown) * evSliderParams.slideWidth) * -1;

                evSliderParams.count = 0;
            }

            function eventsSliderButtons(event){
                var controlButton = $(event.target);

                if($(controlButton).hasClass("prev")){
                      //ONLY SLIDE IF THE FIRST SLIDE IS NOT IN THE FIRST POSITION
                      if(evSliderParams.count != 0){
                          $(evSliderParams.slideContainer).addClass("animating");
                          $(evSliderParams.slideContainer).animate({ left: '+='+evSliderParams.slideWidth }, "slow", function() {
                              evSliderParams.count--;
                              eventsAttributes();
                              $(evSliderParams.slideContainer).removeClass("animating");
                          });
                      } else {
                         $(evSliderParams.slideContainer).animate({ left: evSliderParams.endingPos }, "slow", function() {
                            evSliderParams.count = evSliderParams.numEventDays - evSliderParams.numEventsShown;
                            eventsAttributes();
                            $(evSliderParams.slideContainer).removeClass("animating");
                        });
                      }
                } 

                //IF THE NEXT BUTTON IS CLICKED, SLIDE TO THE LEFT
                if($(controlButton).hasClass("next")){
                    if(evSliderParams.count != evSliderParams.numEventDays - evSliderParams.numEventsShown ){
                        $(evSliderParams.slideContainer).addClass("animating");
                        $(evSliderParams.slideContainer).animate({ left: '-='+evSliderParams.slideWidth }, "slow", function() {
                            evSliderParams.count++;
                            eventsAttributes();
                            $(evSliderParams.slideContainer).removeClass("animating");
                        }); 
                    } else {
                        $(evSliderParams.slideContainer).animate({ left: 0 }, "slow", function() {
                            evSliderParams.count = 0;
                            eventsAttributes();
                            $(evSliderParams.slideContainer).removeClass("animating");
                        });
                    }   
                }
            }
        },

        "Footer": function() {
            // FOR SCOPE
            var template = this;

            // MOVE Bb FOOTER STUFF
            $(".gb-bb-footer.logo").html($("#sw-footer-logo").html());
            $(".gb-bb-footer.links").append($("#sw-footer-links li:eq(0) a"));
            $(".gb-bb-footer.links").append($("#sw-footer-links li:eq(2) a"));
            $(".gb-bb-footer.links").append($("#sw-footer-links li:eq(1) a"));
            $(".gb-bb-footer.links").append($("#sw-footer-copyright"));     

            // FOOTER LINKS
            $(".ac-dropdown-container .selector").on('click keypress', function(event){
                if(template.AllyClick(event) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                    controlDropdowns(event);
                }
            })

            //GLOBALLY CLOSE DROPDOWNS WHEN HOVERED AWAY FROM
            $(".ac-dropdown-container").hover(function(e) {
                e.preventDefault();
            }, function() {

                $(".ac-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
                $(this).removeClass("open"); 
                $(this).find(".selector").attr("aria-expanded","false");
                $(this).parent().removeClass("open");
            })

            function controlDropdowns(event){
                var dropdownToOpen = $(event.currentTarget).parent().find(".ac-dropdown-list").attr("id");
                if($("#"+dropdownToOpen).parent().parent().hasClass("open")){
                    $("#"+dropdownToOpen).parent().attr("aria-hidden", "true").slideUp(300, "swing"); 
                    $("#"+dropdownToOpen).parent().parent().removeClass("open");
                    $("#"+dropdownToOpen).parent().prev().attr("aria-expanded","false");
                    $("#"+dropdownToOpen).closest(".mystart-column").removeClass("open");

                } else {
                    $("#"+dropdownToOpen).parent().attr("aria-hidden","false").slideDown(300, "swing", function(){
                        setTimeout(function(){
                            $("#"+dropdownToOpen).find("li:first a").focus();
                        }, 100);
                    });
                    $("#"+dropdownToOpen).parent().parent().addClass("open");
                    $("#"+dropdownToOpen).parent().prev().attr("aria-expanded","true");
                    $("#"+dropdownToOpen).closest(".mystart-column").addClass("open");
                }
            }

            //FOOTER LOGO
            if("001u000000dddhmiab" == "eisd"){
                $("#district-footer-logo").show();
            } else {
                $("#schools-footer-logo").show();
            }

            //BACK TO TOP
            var toTopPos = $("footer").height() - 77;
            $("#to-top").css("bottom",toTopPos);

            $("#to-top").click(function(e){
                e.preventDefault();
                        $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                            $("li.sw-channel-item:first-child > a").focus();
                        });
            });

